BibliaJFA.proverbios = {
	libro: "Provérbios",
	numCapitulos: 31,

c1: {
versiculos:[
{name:'2011',l:'20',c:'1',v:'1',texto:"Provérbios de Salomão, filho de Davi, rei de Israel." },
{name:'2012',l:'20',c:'1',v:'2',texto:"Para se conhecer a sabedoria e a instrução; para se entenderem as palavras da prudência;" },
{name:'2013',l:'20',c:'1',v:'3',texto:"para se receber a instrução do entendimento, a justiça, o juízo e a eqüidade;" },
{name:'2014',l:'20',c:'1',v:'4',texto:"para dar aos simples prudência, e aos jovens conhecimento e bom siso;" },
{name:'2015',l:'20',c:'1',v:'5',texto:"para o sábio ouvir e crescer em sabedoria, e o instruído adquirir sábios conselhos;" },
{name:'2016',l:'20',c:'1',v:'6',texto:"para entender provérbios e sua interpretação, como também as palavras dos sábios e suas adivinhações." },
{name:'2017',l:'20',c:'1',v:'7',texto:"O temor do SENHOR é o princípio da ciência; os loucos desprezam a sabedoria e a instrução." },
{name:'2018',l:'20',c:'1',v:'8',texto:"Filho meu, ouve a instrução de teu pai e não deixes a doutrina de tua mãe." },
{name:'2019',l:'20',c:'1',v:'9',texto:"Porque diadema de graça serão para a tua cabeça e colares para o teu pescoço." },
{name:'20110',l:'20',c:'1',v:'10',texto:"Filho meu, se os pecadores, com blandícias, te quiserem tentar, não consintas." },
{name:'20111',l:'20',c:'1',v:'11',texto:"Se disserem: Vem conosco, espiemos o sangue, espreitemos sem razão os inocentes," },
{name:'20112',l:'20',c:'1',v:'12',texto:"traguemo-los vivos, como a sepultura, e inteiros, como os que descem à cova;" },
{name:'20113',l:'20',c:'1',v:'13',texto:"acharemos toda sorte de fazenda preciosa; encheremos as nossas casas de despojos;" },
{name:'20114',l:'20',c:'1',v:'14',texto:"lançarás a tua sorte entre nós; teremos todos uma só bolsa." },
{name:'20115',l:'20',c:'1',v:'15',texto:"Filho meu, não te ponhas a caminho com eles; desvia o teu pé das suas veredas." },
{name:'20116',l:'20',c:'1',v:'16',texto:"Porque os pés deles correm para o mal e se apressam a derramar sangue." },
{name:'20117',l:'20',c:'1',v:'17',texto:"Na verdade, debalde se estenderia a rede perante os olhos de qualquer ave." },
{name:'20118',l:'20',c:'1',v:'18',texto:"E estes armam ciladas contra o seu próprio sangue; e a sua própria vida espreitam." },
{name:'20119',l:'20',c:'1',v:'19',texto:"Tais são as veredas de todo aquele que se entrega à cobiça; ela prenderá a alma dos que a possuem." },
{name:'20120',l:'20',c:'1',v:'20',texto:"A suprema Sabedoria altissonantemente clama de fora; pelas ruas levanta a sua voz." },
{name:'20121',l:'20',c:'1',v:'21',texto:"Nas encruzilhadas, no meio dos tumultos, clama; às entradas das portas e na cidade profere as suas palavras:" },
{name:'20122',l:'20',c:'1',v:'22',texto:"Até quando, ó néscios, amareis a necedade? E vós, escarnecedores, desejareis o escárnio? E vós, loucos, aborrecereis o conhecimento?" },
{name:'20123',l:'20',c:'1',v:'23',texto:"Convertei-vos pela minha repreensão; eis que abundantemente derramarei sobre vós meu espírito e vos farei saber as minhas palavras." },
{name:'20124',l:'20',c:'1',v:'24',texto:"Mas, porque clamei, e vós recusastes; porque estendi a minha mão, e não houve quem desse atenção;" },
{name:'20125',l:'20',c:'1',v:'25',texto:"antes, rejeitastes todo o meu conselho e não quisestes a minha repreensão;" },
{name:'20126',l:'20',c:'1',v:'26',texto:"também eu me rirei na vossa perdição e zombarei, vindo o vosso temor," },
{name:'20127',l:'20',c:'1',v:'27',texto:"vindo como assolação o vosso temor, e vindo a vossa perdição como tormenta, sobrevindo-vos aperto e angústia." },
{name:'20128',l:'20',c:'1',v:'28',texto:"Então, a mim clamarão, mas eu não responderei; de madrugada me buscarão, mas não me acharão." },
{name:'20129',l:'20',c:'1',v:'29',texto:"Porquanto aborreceram o conhecimento e não preferiram o temor do SENHOR;" },
{name:'20130',l:'20',c:'1',v:'30',texto:"não quiseram o meu conselho e desprezaram toda a minha repreensão." },
{name:'20131',l:'20',c:'1',v:'31',texto:"Portanto, comerão do fruto do seu caminho e fartar-se-ão dos seus próprios conselhos." },
{name:'20132',l:'20',c:'1',v:'32',texto:"Porque o desvio dos simples os matará, e a prosperidade dos loucos os destruirá." },
{name:'20133',l:'20',c:'1',v:'33',texto:"Mas o que me der ouvidos habitará seguramente e estará descansado do temor do mal." },
],
numVersiculos: 33,
},

c2: {
versiculos:[
{name:'2021',l:'20',c:'2',v:'1',texto:"Filho meu, se aceitares as minhas palavras e esconderes contigo os meus mandamentos," },
{name:'2022',l:'20',c:'2',v:'2',texto:"para fazeres atento à sabedoria o teu ouvido, e para inclinares o teu coração ao entendimento," },
{name:'2023',l:'20',c:'2',v:'3',texto:"e, se clamares por entendimento, e por inteligência alçares a tua voz," },
{name:'2024',l:'20',c:'2',v:'4',texto:"se como a prata a buscares e como a tesouros escondidos a procurares," },
{name:'2025',l:'20',c:'2',v:'5',texto:"então, entenderás o temor do SENHOR e acharás o conhecimento de Deus." },
{name:'2026',l:'20',c:'2',v:'6',texto:"Porque o SENHOR dá a sabedoria, e da sua boca vem o conhecimento e o entendimento." },
{name:'2027',l:'20',c:'2',v:'7',texto:"Ele reserva a verdadeira sabedoria para os retos; escudo é para os que caminham na sinceridade," },
{name:'2028',l:'20',c:'2',v:'8',texto:"para que guarde as veredas do juízo e conserve o caminho dos seus santos." },
{name:'2029',l:'20',c:'2',v:'9',texto:"Então, entenderás justiça, e juízo, e eqüidade, e todas as boas veredas." },
{name:'20210',l:'20',c:'2',v:'10',texto:"Porquanto a sabedoria entrará no teu coração, e o conhecimento será suave à tua alma." },
{name:'20211',l:'20',c:'2',v:'11',texto:"O bom siso te guardará, e a inteligência te conservará;" },
{name:'20212',l:'20',c:'2',v:'12',texto:"para te livrar do mau caminho e do homem que diz coisas perversas;" },
{name:'20213',l:'20',c:'2',v:'13',texto:"dos que deixam as veredas da retidão, para andarem pelos caminhos das trevas;" },
{name:'20214',l:'20',c:'2',v:'14',texto:"que se alegram de mal fazer e folgam com as perversidades dos maus," },
{name:'20215',l:'20',c:'2',v:'15',texto:"cujas veredas são tortuosas e desviadas nas suas carreiras;" },
{name:'20216',l:'20',c:'2',v:'16',texto:"para te livrar da mulher estranha e da estrangeira, que lisonjeia com suas palavras," },
{name:'20217',l:'20',c:'2',v:'17',texto:"a qual deixa o guia da sua mocidade e se esquece do concerto do seu Deus;" },
{name:'20218',l:'20',c:'2',v:'18',texto:"porque a sua casa se inclina para a morte, e as suas veredas, para os mortos;" },
{name:'20219',l:'20',c:'2',v:'19',texto:"todos os que se dirigem a elas não voltarão e não atinarão com as veredas da vida." },
{name:'20220',l:'20',c:'2',v:'20',texto:"Para que andes pelo caminho dos bons e guardes as veredas dos justos." },
{name:'20221',l:'20',c:'2',v:'21',texto:"Porque os retos habitarão a terra, e os sinceros permanecerão nela." },
{name:'20222',l:'20',c:'2',v:'22',texto:"Mas os ímpios serão arrancados da terra, e os aleivosos serão dela exterminados." },
],
numVersiculos: 22,
},

c3: {
versiculos:[
{name:'2031',l:'20',c:'3',v:'1',texto:"Filho meu, não te esqueças da minha lei, e o teu coração guarde os meus mandamentos." },
{name:'2032',l:'20',c:'3',v:'2',texto:"Porque eles aumentarão os teus dias e te acrescentarão anos de vida e paz." },
{name:'2033',l:'20',c:'3',v:'3',texto:"Não te desamparem a benignidade e a fidelidade; ata-as ao teu pescoço; escreve-as na tábua do teu coração" },
{name:'2034',l:'20',c:'3',v:'4',texto:"e acharás graça e bom entendimento aos olhos de Deus e dos homens." },
{name:'2035',l:'20',c:'3',v:'5',texto:"Confia no SENHOR de todo o teu coração e não te estribes no teu próprio entendimento." },
{name:'2036',l:'20',c:'3',v:'6',texto:"Reconhece-o em todos os teus caminhos, e ele endireitará as tuas veredas." },
{name:'2037',l:'20',c:'3',v:'7',texto:"Não sejas sábio a teus próprios olhos; teme ao SENHOR e aparta-te do mal." },
{name:'2038',l:'20',c:'3',v:'8',texto:"Isso será remédio para o teu umbigo e medula para os teus ossos." },
{name:'2039',l:'20',c:'3',v:'9',texto:"Honra ao SENHOR com a tua fazenda e com as primícias de toda a tua renda;" },
{name:'20310',l:'20',c:'3',v:'10',texto:"e se encherão os teus celeiros abundantemente, e trasbordarão de mosto os teus lagares." },
{name:'20311',l:'20',c:'3',v:'11',texto:"Filho meu, não rejeites a correção do SENHOR, nem te enojes da sua repreensão." },
{name:'20312',l:'20',c:'3',v:'12',texto:"Porque o SENHOR repreende aquele a quem ama, assim como o pai, ao filho a quem quer bem." },
{name:'20313',l:'20',c:'3',v:'13',texto:"Bem-aventurado o homem que acha sabedoria, e o homem que adquire conhecimento." },
{name:'20314',l:'20',c:'3',v:'14',texto:"Porque melhor é a sua mercadoria do que a mercadoria de prata, e a sua renda do que o ouro mais fino." },
{name:'20315',l:'20',c:'3',v:'15',texto:"Mais preciosa é do que os rubins; e tudo o que podes desejar não se pode comparar a ela." },
{name:'20316',l:'20',c:'3',v:'16',texto:"Aumento de dias há na sua mão direita; na sua esquerda, riquezas e honra." },
{name:'20317',l:'20',c:'3',v:'17',texto:"Os seus caminhos são caminhos de delícias, e todas as suas veredas, paz." },
{name:'20318',l:'20',c:'3',v:'18',texto:"É árvore da vida para os que a seguram, e bem-aventurados são todos os que a retêm." },
{name:'20319',l:'20',c:'3',v:'19',texto:"O SENHOR, com sabedoria, fundou a terra; preparou os céus com inteligência." },
{name:'20320',l:'20',c:'3',v:'20',texto:"Pelo seu conhecimento, se fenderam os abismos, e as nuvens destilam o orvalho." },
{name:'20321',l:'20',c:'3',v:'21',texto:"Filho meu, não se apartem estas coisas dos teus olhos; guarda a verdadeira sabedoria e o bom siso;" },
{name:'20322',l:'20',c:'3',v:'22',texto:"porque serão vida para a tua alma e graça, para o teu pescoço." },
{name:'20323',l:'20',c:'3',v:'23',texto:"Então, andarás com confiança no teu caminho, e não tropeçará o teu pé." },
{name:'20324',l:'20',c:'3',v:'24',texto:"Quando te deitares, não temerás; sim, tu te deitarás, e o teu sono será suave." },
{name:'20325',l:'20',c:'3',v:'25',texto:"Não temas o pavor repentino, nem a assolação dos ímpios quando vier." },
{name:'20326',l:'20',c:'3',v:'26',texto:"Porque o SENHOR será a tua esperança e guardará os teus pés de serem presos." },
{name:'20327',l:'20',c:'3',v:'27',texto:"Não detenhas dos seus donos o bem, estando na tua mão poder fazê-lo." },
{name:'20328',l:'20',c:'3',v:'28',texto:"Não digas ao teu próximo: Vai e torna, e amanhã to darei, tendo-o tu contigo." },
{name:'20329',l:'20',c:'3',v:'29',texto:"Não maquines mal contra o teu próximo, pois habita contigo confiadamente." },
{name:'20330',l:'20',c:'3',v:'30',texto:"Não contendas com alguém sem razão, se te não tem feito mal." },
{name:'20331',l:'20',c:'3',v:'31',texto:"Não tenhas inveja do homem violento, nem escolhas nenhum de seus caminhos." },
{name:'20332',l:'20',c:'3',v:'32',texto:"Porque o perverso é abominação para o SENHOR, mas com os sinceros está o seu segredo." },
{name:'20333',l:'20',c:'3',v:'33',texto:"A maldição do SENHOR habita na casa do ímpio, mas a habitação dos justos ele abençoará." },
{name:'20334',l:'20',c:'3',v:'34',texto:"Certamente ele escarnecerá dos escarnecedores, mas dará graça aos mansos." },
{name:'20335',l:'20',c:'3',v:'35',texto:"Os sábios herdarão honra, mas os loucos tomam sobre si confusão." },
],
numVersiculos: 35,
},

c4: {
versiculos:[
{name:'2041',l:'20',c:'4',v:'1',texto:"Ouvi, filhos, a correção do pai e estai atentos para conhecerdes a prudência." },
{name:'2042',l:'20',c:'4',v:'2',texto:"Pois dou-vos boa doutrina; não deixeis a minha lei." },
{name:'2043',l:'20',c:'4',v:'3',texto:"Porque eu era filho de meu pai, tenro e único em estima diante de minha mãe." },
{name:'2044',l:'20',c:'4',v:'4',texto:"E ele ensinava-me e dizia-me: Retenha as minhas palavras o teu coração; guarda os meus mandamentos e vive." },
{name:'2045',l:'20',c:'4',v:'5',texto:"Adquire a sabedoria, adquire a inteligência e não te esqueças nem te apartes das palavras da minha boca." },
{name:'2046',l:'20',c:'4',v:'6',texto:"Não desampares a sabedoria, e ela te guardará; ama-a, e ela te conservará." },
{name:'2047',l:'20',c:'4',v:'7',texto:"A sabedoria é a coisa principal; adquire, pois, a sabedoria; sim, com tudo o que possuis, adquire o conhecimento." },
{name:'2048',l:'20',c:'4',v:'8',texto:"Exalta-a, e ela te exaltará; e, abraçando-a tu, ela te honrará." },
{name:'2049',l:'20',c:'4',v:'9',texto:"Dará à tua cabeça um diadema de graça e uma coroa de glória te entregará." },
{name:'20410',l:'20',c:'4',v:'10',texto:"Ouve, filho meu, e aceita as minhas palavras, e se te multiplicarão os anos de vida." },
{name:'20411',l:'20',c:'4',v:'11',texto:"No caminho da sabedoria, te ensinei e, pelas carreiras direitas, te fiz andar." },
{name:'20412',l:'20',c:'4',v:'12',texto:"Por elas andando, não se embaraçarão os teus passos; e, se correres, não tropeçarás." },
{name:'20413',l:'20',c:'4',v:'13',texto:"Pega-te à correção e não a largues; guarda-a, porque ela é a tua vida." },
{name:'20414',l:'20',c:'4',v:'14',texto:"Não entres na vereda dos ímpios, nem andes pelo caminho dos maus." },
{name:'20415',l:'20',c:'4',v:'15',texto:"Evita-o; não passes por ele; desvia-te dele e passa de largo." },
{name:'20416',l:'20',c:'4',v:'16',texto:"Pois não dormem, se não fizerem mal, e foge deles o sono, se não fizerem tropeçar alguém." },
{name:'20417',l:'20',c:'4',v:'17',texto:"Porque comem o pão da impiedade e bebem o vinho das violências." },
{name:'20418',l:'20',c:'4',v:'18',texto:"Mas a vereda dos justos é como a luz da aurora, que vai brilhando mais e mais até ser dia perfeito." },
{name:'20419',l:'20',c:'4',v:'19',texto:"O caminho dos ímpios é como a escuridão; nem conhecem aquilo em que tropeçam." },
{name:'20420',l:'20',c:'4',v:'20',texto:"Filho meu, atenta para as minhas palavras; às minhas razões inclina o teu ouvido." },
{name:'20421',l:'20',c:'4',v:'21',texto:"Não as deixes apartar-se dos teus olhos; guarda-as no meio do teu coração." },
{name:'20422',l:'20',c:'4',v:'22',texto:"Porque são vida para os que as acham e saúde, para o seu corpo." },
{name:'20423',l:'20',c:'4',v:'23',texto:"Sobre tudo o que se deve guardar, guarda o teu coração, porque dele procedem as saídas da vida." },
{name:'20424',l:'20',c:'4',v:'24',texto:"Desvia de ti a tortuosidade da boca e alonga de ti a perversidade dos lábios." },
{name:'20425',l:'20',c:'4',v:'25',texto:"Os teus olhos olhem direitos, e as tuas pálpebras olhem diretamente diante de ti." },
{name:'20426',l:'20',c:'4',v:'26',texto:"Pondera a vereda de teus pés, e todos os teus caminhos sejam bem ordenados!" },
{name:'20427',l:'20',c:'4',v:'27',texto:"Não declines nem para a direita nem para a esquerda; retira o teu pé do mal." },
],
numVersiculos: 27,
},

c5: {
versiculos:[
{name:'2051',l:'20',c:'5',v:'1',texto:"Filho meu, atende à minha sabedoria; à minha razão inclina o teu ouvido;" },
{name:'2052',l:'20',c:'5',v:'2',texto:"para que conserves os meus avisos, e os teus lábios guardem o conhecimento." },
{name:'2053',l:'20',c:'5',v:'3',texto:"Porque os lábios da mulher estranha destilam favos de mel, e o seu paladar é mais macio do que o azeite;" },
{name:'2054',l:'20',c:'5',v:'4',texto:"mas o seu fim é amargoso como o absinto, agudo como a espada de dois fios." },
{name:'2055',l:'20',c:'5',v:'5',texto:"Os seus pés descem à morte; os seus passos firmam-se no inferno." },
{name:'2056',l:'20',c:'5',v:'6',texto:"Ela não pondera a vereda da vida; as suas carreiras são variáveis, e não as conhece." },
{name:'2057',l:'20',c:'5',v:'7',texto:"Agora, pois, filhos, dai-me ouvidos e não vos desvieis das palavras da minha boca." },
{name:'2058',l:'20',c:'5',v:'8',texto:"Afasta dela o teu caminho e não te aproximes da porta da sua casa;" },
{name:'2059',l:'20',c:'5',v:'9',texto:"para que não dês a outros a tua honra, nem os teus anos a cruéis." },
{name:'20510',l:'20',c:'5',v:'10',texto:"Para que não se fartem os estranhos do teu poder, e todos os teus trabalhos entrem na casa do estrangeiro;" },
{name:'20511',l:'20',c:'5',v:'11',texto:"e gemas no teu fim, quando se consumirem a tua carne e o teu corpo," },
{name:'20512',l:'20',c:'5',v:'12',texto:"e digas: Como aborreci a correção! E desprezou o meu coração a repreensão!" },
{name:'20513',l:'20',c:'5',v:'13',texto:"E não escutei a voz dos meus ensinadores, nem a meus mestres inclinei o meu ouvido!" },
{name:'20514',l:'20',c:'5',v:'14',texto:"Quase que em todo o mal me achei no meio da congregação e do ajuntamento." },
{name:'20515',l:'20',c:'5',v:'15',texto:"Bebe a água da tua cisterna e das correntes do teu poço." },
{name:'20516',l:'20',c:'5',v:'16',texto:"Derramar-se-iam por fora as tuas fontes, e pelas ruas, os ribeiros de águas?" },
{name:'20517',l:'20',c:'5',v:'17',texto:"Sejam para ti só e não para os estranhos contigo." },
{name:'20518',l:'20',c:'5',v:'18',texto:"Seja bendito o teu manancial, e alegra-te com a mulher da tua mocidade," },
{name:'20519',l:'20',c:'5',v:'19',texto:"como cerva amorosa e gazela graciosa; saciem-te os seus seios em todo o tempo; e pelo seu amor sê atraído perpetuamente." },
{name:'20520',l:'20',c:'5',v:'20',texto:"E por que, filho meu, andarias atraído pela estranha e abraçarias o seio da estrangeira?" },
{name:'20521',l:'20',c:'5',v:'21',texto:"Porque os caminhos do homem estão perante os olhos do SENHOR, e ele aplana todas as suas carreiras." },
{name:'20522',l:'20',c:'5',v:'22',texto:"Quanto ao ímpio, as suas iniqüidades o prenderão, e, com as cordas do seu pecado, será detido." },
{name:'20523',l:'20',c:'5',v:'23',texto:"Ele morrerá, porque sem correção andou, e, pelo excesso da sua loucura, andará errado." },
],
numVersiculos: 23,
},

c6: {
versiculos:[
{name:'2061',l:'20',c:'6',v:'1',texto:"Filho meu, se ficaste por fiador do teu companheiro, se deste a tua mão ao estranho," },
{name:'2062',l:'20',c:'6',v:'2',texto:"enredaste-te com as palavras da tua boca, prendeste-te com as palavras da tua boca." },
{name:'2063',l:'20',c:'6',v:'3',texto:"Faze, pois, isto agora, filho meu, e livra-te, pois já caíste nas mãos do teu companheiro: vai, humilha-te e importuna o teu companheiro;" },
{name:'2064',l:'20',c:'6',v:'4',texto:"não dês sono aos teus olhos, nem repouso às tuas pálpebras;" },
{name:'2065',l:'20',c:'6',v:'5',texto:"livra-te, como a gazela, da mão do caçador e, como a ave, da mão do passarinheiro." },
{name:'2066',l:'20',c:'6',v:'6',texto:"Vai ter com a formiga, ó preguiçoso; olha para os seus caminhos e sê sábio." },
{name:'2067',l:'20',c:'6',v:'7',texto:"A qual, não tendo superior, nem oficial, nem dominador," },
{name:'2068',l:'20',c:'6',v:'8',texto:"prepara no verão o seu pão; na sega ajunta o seu mantimento." },
{name:'2069',l:'20',c:'6',v:'9',texto:"Ó preguiçoso, até quando ficarás deitado? Quando te levantarás do teu sono?" },
{name:'20610',l:'20',c:'6',v:'10',texto:"Um pouco de sono, um pouco tosquenejando, um pouco encruzando as mãos, para estar deitado," },
{name:'20611',l:'20',c:'6',v:'11',texto:"assim te sobrevirá a tua pobreza como um ladrão, e a tua necessidade, como um homem armado." },
{name:'20612',l:'20',c:'6',v:'12',texto:"O homem de Belial, o homem vicioso, anda em perversidade de boca." },
{name:'20613',l:'20',c:'6',v:'13',texto:"Acena com os olhos, fala com os pés, faz sinais com os dedos." },
{name:'20614',l:'20',c:'6',v:'14',texto:"Perversidade há no seu coração; todo o tempo maquina mal; anda semeando contendas." },
{name:'20615',l:'20',c:'6',v:'15',texto:"Pelo que a sua destruição virá repentinamente; subitamente será quebrantado, sem que haja cura." },
{name:'20616',l:'20',c:'6',v:'16',texto:"Estas seis coisas aborrece o SENHOR, e a sétima a sua alma abomina:" },
{name:'20617',l:'20',c:'6',v:'17',texto:"olhos altivos, e língua mentirosa, e mãos que derramam sangue inocente," },
{name:'20618',l:'20',c:'6',v:'18',texto:"e coração que maquina pensamentos viciosos, e pés que se apressam a correr para o mal," },
{name:'20619',l:'20',c:'6',v:'19',texto:"e testemunha falsa que profere mentiras, e o que semeia contendas entre irmãos." },
{name:'20620',l:'20',c:'6',v:'20',texto:"Filho meu, guarda o mandamento de teu pai e não deixes a lei de tua mãe." },
{name:'20621',l:'20',c:'6',v:'21',texto:"Ata-os perpetuamente ao teu coração e pendura-os ao teu pescoço." },
{name:'20622',l:'20',c:'6',v:'22',texto:"Quando caminhares, isso te guiará; quando te deitares, te guardará; quando acordares, falará contigo." },
{name:'20623',l:'20',c:'6',v:'23',texto:"Porque o mandamento é uma lâmpada, e a lei, uma luz, e as repreensões da correção são o caminho da vida," },
{name:'20624',l:'20',c:'6',v:'24',texto:"para te guardarem da má mulher e das lisonjas da língua estranha." },
{name:'20625',l:'20',c:'6',v:'25',texto:"Não cobices no teu coração a sua formosura, nem te prendas com os seus olhos." },
{name:'20626',l:'20',c:'6',v:'26',texto:"Porque por causa de uma mulher prostituta se chega a pedir um bocado de pão; e a adúltera anda à caça de preciosa vida." },
{name:'20627',l:'20',c:'6',v:'27',texto:"Tomará alguém fogo no seu seio, sem que as suas vestes se queimem?" },
{name:'20628',l:'20',c:'6',v:'28',texto:"Ou andará alguém sobre as brasas, sem que se queimem os seus pés?" },
{name:'20629',l:'20',c:'6',v:'29',texto:"Assim será o que entrar à mulher do seu próximo; não ficará inocente todo aquele que a tocar." },
{name:'20630',l:'20',c:'6',v:'30',texto:"Não se injuria o ladrão, quando furta para saciar a sua alma, tendo fome;" },
{name:'20631',l:'20',c:'6',v:'31',texto:"mas, encontrado, pagará sete vezes tanto; dará toda a fazenda de sua casa." },
{name:'20632',l:'20',c:'6',v:'32',texto:"O que adultera com uma mulher é falto de entendimento; destrói a sua alma o que tal faz." },
{name:'20633',l:'20',c:'6',v:'33',texto:"Achará castigo e vilipêndio, e o seu opróbrio nunca se apagará." },
{name:'20634',l:'20',c:'6',v:'34',texto:"Porque furioso é o ciúme do marido; e de maneira nenhuma perdoará no dia da vingança." },
{name:'20635',l:'20',c:'6',v:'35',texto:"Nenhum resgate aceitará, nem consentirá, ainda que multipliques os presentes." },
],
numVersiculos: 35,
},

c7: {
versiculos:[
{name:'2071',l:'20',c:'7',v:'1',texto:"Filho meu, guarda as minhas palavras e esconde dentro de ti os meus mandamentos." },
{name:'2072',l:'20',c:'7',v:'2',texto:"Guarda os meus mandamentos e vive; e a minha lei, como a menina dos teus olhos." },
{name:'2073',l:'20',c:'7',v:'3',texto:"Ata-os aos teus dedos, escreve-os na tábua do teu coração." },
{name:'2074',l:'20',c:'7',v:'4',texto:"Dize à Sabedoria: Tu és minha irmã; e à prudência chama tua parenta;" },
{name:'2075',l:'20',c:'7',v:'5',texto:"para te guardarem da mulher alheia, da estranha que lisonjeia com as suas palavras." },
{name:'2076',l:'20',c:'7',v:'6',texto:"Porque da janela da minha casa, por minhas grades olhando eu," },
{name:'2077',l:'20',c:'7',v:'7',texto:"vi entre os simples, descobri entre os jovens, um jovem falto de juízo," },
{name:'2078',l:'20',c:'7',v:'8',texto:"que passava pela rua junto à sua esquina e seguia o caminho da sua casa," },
{name:'2079',l:'20',c:'7',v:'9',texto:"no crepúsculo, à tarde do dia, na escuridão e trevas da noite." },
{name:'20710',l:'20',c:'7',v:'10',texto:"E eis que uma mulher lhe saiu ao encontro, com enfeites de prostituta e astuto coração." },
{name:'20711',l:'20',c:'7',v:'11',texto:"Esta era alvoroçadora e contenciosa, e não paravam em casa os seus pés;" },
{name:'20712',l:'20',c:'7',v:'12',texto:"ora pelas ruas, ora pelas praças, espreitando por todos os cantos," },
{name:'20713',l:'20',c:'7',v:'13',texto:"aproximou-se dele, e o beijou, e esforçou o seu rosto, e disse-lhe:" },
{name:'20714',l:'20',c:'7',v:'14',texto:"Sacrifícios pacíficos tenho comigo; hoje paguei os meus votos." },
{name:'20715',l:'20',c:'7',v:'15',texto:"Por isso, saí ao teu encontro, a buscar diligentemente a tua face, e te achei." },
{name:'20716',l:'20',c:'7',v:'16',texto:"Já cobri a minha cama com cobertas de tapeçaria, com obras lavradas com linho fino do Egito;" },
{name:'20717',l:'20',c:'7',v:'17',texto:"já perfumei o meu leito com mirra, aloés e canela." },
{name:'20718',l:'20',c:'7',v:'18',texto:"Vem, saciemo-nos de amores até pela manhã; alegremo-nos com amores." },
{name:'20719',l:'20',c:'7',v:'19',texto:"Porque o marido não está em casa, foi fazer uma jornada ao longe." },
{name:'20720',l:'20',c:'7',v:'20',texto:"Um saquitel de dinheiro levou na sua mão; só no dia marcado voltará a casa." },
{name:'20721',l:'20',c:'7',v:'21',texto:"Seduziu-o com a multidão das suas palavras, com as lisonjas dos seus lábios o persuadiu." },
{name:'20722',l:'20',c:'7',v:'22',texto:"E ele segue-a logo, como boi que vai ao matadouro; e, como o louco ao castigo das prisões," },
{name:'20723',l:'20',c:'7',v:'23',texto:"até que a flecha lhe atravesse o fígado, como a ave que se apressa para o laço e não sabe que ele está ali contra a sua vida." },
{name:'20724',l:'20',c:'7',v:'24',texto:"Agora, pois, filhos, dai-me ouvidos e estai atentos às palavras da minha boca;" },
{name:'20725',l:'20',c:'7',v:'25',texto:"não se desvie para os seus caminhos o teu coração, e não andes perdido nas suas veredas;" },
{name:'20726',l:'20',c:'7',v:'26',texto:"porque a muitos feridos derribou; e são muitíssimos os que por ela foram mortos." },
{name:'20727',l:'20',c:'7',v:'27',texto:"Caminhos de sepultura é a sua casa, os quais descem às câmaras da morte." },
],
numVersiculos: 27,
},

c8: {
versiculos:[
{name:'2081',l:'20',c:'8',v:'1',texto:"Não clama, porventura, a Sabedoria? E a Inteligência não dá a sua voz?" },
{name:'2082',l:'20',c:'8',v:'2',texto:"No cume das alturas, junto ao caminho, nas encruzilhadas das veredas, ela se coloca." },
{name:'2083',l:'20',c:'8',v:'3',texto:"Da banda das portas da cidade, à entrada da cidade e à entrada das portas está clamando:" },
{name:'2084',l:'20',c:'8',v:'4',texto:"A vós, ó homens, clamo; e a minha voz se dirige aos filhos dos homens." },
{name:'2085',l:'20',c:'8',v:'5',texto:"Entendei, ó simples, a prudência; e vós, loucos, entendei de coração." },
{name:'2086',l:'20',c:'8',v:'6',texto:"Ouvi, porque proferirei coisas excelentes; os meus lábios se abrirão para a eqüidade." },
{name:'2087',l:'20',c:'8',v:'7',texto:"Porque a minha boca proferirá a verdade; os meus lábios abominam a impiedade." },
{name:'2088',l:'20',c:'8',v:'8',texto:"Em justiça são todas as palavras da minha boca; não há nelas nenhuma coisa tortuosa nem perversa." },
{name:'2089',l:'20',c:'8',v:'9',texto:"Todas elas são retas para o que bem as entende e justas, para os que acham o conhecimento." },
{name:'20810',l:'20',c:'8',v:'10',texto:"Aceitai a minha correção, e não a prata, e o conhecimento mais do que o ouro fino escolhido." },
{name:'20811',l:'20',c:'8',v:'11',texto:"Porque melhor é a sabedoria do que os rubins; e de tudo o que se deseja nada se pode comparar com ela." },
{name:'20812',l:'20',c:'8',v:'12',texto:"Eu, a Sabedoria, habito com a prudência e acho a ciência dos conselhos." },
{name:'20813',l:'20',c:'8',v:'13',texto:"O temor do SENHOR é aborrecer o mal; a soberba, e a arrogância, e o mau caminho, e a boca perversa aborreço." },
{name:'20814',l:'20',c:'8',v:'14',texto:"Meu é o conselho e a verdadeira sabedoria; eu sou o entendimento, minha é a fortaleza." },
{name:'20815',l:'20',c:'8',v:'15',texto:"Por mim, reinam os reis, e os príncipes ordenam justiça." },
{name:'20816',l:'20',c:'8',v:'16',texto:"Por mim governam os príncipes e os nobres; sim, todos os juízes da terra." },
{name:'20817',l:'20',c:'8',v:'17',texto:"Eu amo os que me amam, e os que de madrugada me buscam me acharão." },
{name:'20818',l:'20',c:'8',v:'18',texto:"Riquezas e honra estão comigo; sim, riquezas duráveis e justiça." },
{name:'20819',l:'20',c:'8',v:'19',texto:"Melhor é o meu fruto do que o ouro, sim, do que o ouro refinado; e as minhas novidades, melhores do que a prata escolhida." },
{name:'20820',l:'20',c:'8',v:'20',texto:"Faço andar pelo caminho da justiça, no meio das veredas do juízo." },
{name:'20821',l:'20',c:'8',v:'21',texto:"Para fazer herdar bens permanentes aos que me amam e encher os seus tesouros." },
{name:'20822',l:'20',c:'8',v:'22',texto:"O SENHOR me possuiu no princípio de seus caminhos e antes de suas obras mais antigas." },
{name:'20823',l:'20',c:'8',v:'23',texto:"Desde a eternidade, fui ungida; desde o princípio, antes do começo da terra." },
{name:'20824',l:'20',c:'8',v:'24',texto:"Antes de haver abismos, fui gerada; e antes ainda de haver fontes carregadas de águas." },
{name:'20825',l:'20',c:'8',v:'25',texto:"Antes que os montes fossem firmados, antes dos outeiros, eu fui gerada." },
{name:'20826',l:'20',c:'8',v:'26',texto:"Ainda ele não tinha feito a terra, nem os campos, nem sequer o princípio do pó do mundo." },
{name:'20827',l:'20',c:'8',v:'27',texto:"Quando ele preparava os céus, aí estava eu; quando compassava ao redor a face do abismo;" },
{name:'20828',l:'20',c:'8',v:'28',texto:"quando firmava as nuvens de cima, quando fortificava as fontes do abismo;" },
{name:'20829',l:'20',c:'8',v:'29',texto:"quando punha ao mar o seu termo, para que as águas não trespassassem o seu mando; quando compunha os fundamentos da terra," },
{name:'20830',l:'20',c:'8',v:'30',texto:"então, eu estava com ele e era seu aluno; e era cada dia as suas delícias, folgando perante ele em todo o tempo," },
{name:'20831',l:'20',c:'8',v:'31',texto:"folgando no seu mundo habitável e achando as minhas delícias com os filhos dos homens." },
{name:'20832',l:'20',c:'8',v:'32',texto:"Agora, pois, filhos, ouvi-me, porque bem-aventurados serão os que guardarem os meus caminhos." },
{name:'20833',l:'20',c:'8',v:'33',texto:"Ouvi a correção, não a rejeiteis e sede sábios." },
{name:'20834',l:'20',c:'8',v:'34',texto:"Bem-aventurado o homem que me dá ouvidos, velando às minhas portas cada dia, esperando às ombreiras da minha entrada." },
{name:'20835',l:'20',c:'8',v:'35',texto:"Porque o que me achar achará a vida e alcançará favor do SENHOR." },
{name:'20836',l:'20',c:'8',v:'36',texto:"Mas o que pecar contra mim violentará a sua própria alma; todos os que me aborrecem amam a morte." },
],
numVersiculos: 36,
},

c9: {
versiculos:[
{name:'2091',l:'20',c:'9',v:'1',texto:"A sabedoria já edificou a sua casa, já lavrou as suas sete colunas." },
{name:'2092',l:'20',c:'9',v:'2',texto:"Já sacrificou as suas vítimas, misturou o seu vinho e já preparou a sua mesa." },
{name:'2093',l:'20',c:'9',v:'3',texto:"Já deu ordens às suas criadas, já anda convidando desde as alturas da cidade, dizendo:" },
{name:'2094',l:'20',c:'9',v:'4',texto:"Quem é simples volte-se para aqui. Aos faltos de entendimento diz:" },
{name:'2095',l:'20',c:'9',v:'5',texto:"Vinde, comei do meu pão e bebei do vinho que tenho misturado." },
{name:'2096',l:'20',c:'9',v:'6',texto:"Deixai os insensatos, e vivei, e andai pelo caminho do entendimento." },
{name:'2097',l:'20',c:'9',v:'7',texto:"O que repreende o escarnecedor afronta toma para si; e o que censura o ímpio recebe a sua mancha." },
{name:'2098',l:'20',c:'9',v:'8',texto:"Não repreendas o escarnecedor, para que te não aborreça; repreende o sábio, e amar-te-á." },
{name:'2099',l:'20',c:'9',v:'9',texto:"Dá instrução ao sábio, e ele se fará mais sábio; ensina ao justo, e ele crescerá em entendimento." },
{name:'20910',l:'20',c:'9',v:'10',texto:"O temor do SENHOR é o princípio da sabedoria, e a ciência do Santo, a prudência." },
{name:'20911',l:'20',c:'9',v:'11',texto:"Porque, por mim, se multiplicam os teus dias, e anos de vida se te acrescentarão." },
{name:'20912',l:'20',c:'9',v:'12',texto:"Se fores sábio, para ti sábio serás; e, se fores escarnecedor, tu só o suportarás." },
{name:'20913',l:'20',c:'9',v:'13',texto:"A mulher louca é alvoroçadora; é néscia e não sabe coisa alguma." },
{name:'20914',l:'20',c:'9',v:'14',texto:"E assenta-se à porta da sua casa ou numa cadeira, nas alturas da cidade," },
{name:'20915',l:'20',c:'9',v:'15',texto:"para chamar os que passam e seguem direito o seu caminho." },
{name:'20916',l:'20',c:'9',v:'16',texto:"Quem é simples, volte-se para aqui. E aos faltos de entendimento diz:" },
{name:'20917',l:'20',c:'9',v:'17',texto:"As águas roubadas são doces, e o pão comido às ocultas é suave." },
{name:'20918',l:'20',c:'9',v:'18',texto:"Mas não sabem que ali estão os mortos, que os seus convidados estão nas profundezas do inferno." },
],
numVersiculos: 18,
},

c10: {
versiculos:[
{name:'20101',l:'20',c:'10',v:'1',texto:"Provérbios de Salomão. O filho sábio alegra a seu pai, mas o filho louco é a tristeza de sua mãe." },
{name:'20102',l:'20',c:'10',v:'2',texto:"Os tesouros da impiedade de nada aproveitam; mas a justiça livra da morte." },
{name:'20103',l:'20',c:'10',v:'3',texto:"O SENHOR não deixa ter fome a alma do justo, mas o desejo dos ímpios rechaça." },
{name:'20104',l:'20',c:'10',v:'4',texto:"O que trabalha com mão enganosa empobrece, mas a mão dos diligentes enriquece." },
{name:'20105',l:'20',c:'10',v:'5',texto:"O que ajunta no verão é filho sábio, mas o que dorme na sega é filho que envergonha." },
{name:'20106',l:'20',c:'10',v:'6',texto:"Bênçãos há sobre a cabeça do justo, mas a violência cobre a boca dos ímpios." },
{name:'20107',l:'20',c:'10',v:'7',texto:"A memória do justo é abençoada, mas o nome dos ímpios apodrecerá." },
{name:'20108',l:'20',c:'10',v:'8',texto:"O sábio de coração aceita os mandamentos, mas o louco palrador será transtornado." },
{name:'20109',l:'20',c:'10',v:'9',texto:"Quem anda em sinceridade anda seguro, mas o que perverte os seus caminhos será conhecido." },
{name:'201010',l:'20',c:'10',v:'10',texto:"O que acena com os olhos dá dores, e o tolo de lábios será transtornado." },
{name:'201011',l:'20',c:'10',v:'11',texto:"A boca do justo é manancial de vida, mas a violência cobre a boca dos ímpios." },
{name:'201012',l:'20',c:'10',v:'12',texto:"O ódio excita contendas, mas o amor cobre todas as transgressões." },
{name:'201013',l:'20',c:'10',v:'13',texto:"Nos lábios do sábio se acha a sabedoria, mas a vara é para as costas do falto de entendimento." },
{name:'201014',l:'20',c:'10',v:'14',texto:"Os sábios escondem a sabedoria, mas a boca do tolo é uma destruição." },
{name:'201015',l:'20',c:'10',v:'15',texto:"A fazenda do rico é a cidade da sua fortaleza; a destruição dos pobres é a sua pobreza." },
{name:'201016',l:'20',c:'10',v:'16',texto:"A obra do justo conduz à vida, as produções do ímpio, ao pecado." },
{name:'201017',l:'20',c:'10',v:'17',texto:"O caminho para a vida é daquele que guarda a correção, mas o que abandona a repreensão erra." },
{name:'201018',l:'20',c:'10',v:'18',texto:"O que encobre o ódio tem lábios falsos, e o que difama é um insensato." },
{name:'201019',l:'20',c:'10',v:'19',texto:"Na multidão de palavras não falta transgressão, mas o que modera os seus lábios é prudente." },
{name:'201020',l:'20',c:'10',v:'20',texto:"Prata escolhida é a língua do justo; o coração dos ímpios é de nenhum preço." },
{name:'201021',l:'20',c:'10',v:'21',texto:"Os lábios do justo apascentam muitos, mas os tolos, por falta de entendimento, morrem." },
{name:'201022',l:'20',c:'10',v:'22',texto:"A bênção do SENHOR é que enriquece, e ele não acrescenta dores." },
{name:'201023',l:'20',c:'10',v:'23',texto:"Um divertimento é para o tolo praticar a iniqüidade; para o homem inteligente, o mesmo é o ser sábio." },
{name:'201024',l:'20',c:'10',v:'24',texto:"O temor do ímpio virá sobre ele, mas o desejo dos justos Deus o cumprirá." },
{name:'201025',l:'20',c:'10',v:'25',texto:"Como a tempestade, assim passa o ímpio, mas o justo tem perpétuo fundamento." },
{name:'201026',l:'20',c:'10',v:'26',texto:"Como vinagre para os dentes, como fumaça para os olhos, assim é o preguiçoso para aqueles que o mandam." },
{name:'201027',l:'20',c:'10',v:'27',texto:"O temor do SENHOR aumenta os dias, mas os anos dos ímpios serão abreviados." },
{name:'201028',l:'20',c:'10',v:'28',texto:"A esperança dos justos é alegria, mas a expectação dos ímpios perecerá." },
{name:'201029',l:'20',c:'10',v:'29',texto:"O caminho do SENHOR é fortaleza para os retos, mas ruína virá aos que praticam a iniqüidade." },
{name:'201030',l:'20',c:'10',v:'30',texto:"O justo nunca será abalado, mas os ímpios não habitarão a terra." },
{name:'201031',l:'20',c:'10',v:'31',texto:"A boca do justo produz sabedoria em abundância, mas a língua da perversidade será desarraigada." },
{name:'201032',l:'20',c:'10',v:'32',texto:"Os lábios do justo sabem o que agrada, mas a boca dos ímpios anda cheia de perversidades." },
],
numVersiculos: 32,
},

c11: {
versiculos:[
{name:'20111',l:'20',c:'11',v:'1',texto:"Balança enganosa é abominação para o SENHOR, mas o peso justo é o seu prazer." },
{name:'20112',l:'20',c:'11',v:'2',texto:"Vindo a soberba, virá também a afronta; mas com os humildes está a sabedoria." },
{name:'20113',l:'20',c:'11',v:'3',texto:"A sinceridade dos sinceros os encaminhará, mas a perversidade dos desleais os destruirá." },
{name:'20114',l:'20',c:'11',v:'4',texto:"Não aproveitam as riquezas no dia da ira, mas a justiça livra da morte." },
{name:'20115',l:'20',c:'11',v:'5',texto:"A justiça do sincero endireitará o seu caminho, mas o ímpio, pela sua impiedade, cairá." },
{name:'20116',l:'20',c:'11',v:'6',texto:"A justiça dos virtuosos os livrará, mas, na sua perversidade, serão apanhados os iníquos." },
{name:'20117',l:'20',c:'11',v:'7',texto:"Morrendo o homem ímpio, perece a sua expectação, e a esperança da iniqüidade perde-se." },
{name:'20118',l:'20',c:'11',v:'8',texto:"O justo é libertado da angústia, e o ímpio fica em seu lugar." },
{name:'20119',l:'20',c:'11',v:'9',texto:"O hipócrita, com a boca, danifica o seu próximo, mas os justos são libertados pelo conhecimento." },
{name:'201110',l:'20',c:'11',v:'10',texto:"No bem dos justos, exulta a cidade; e, perecendo os ímpios, há júbilo." },
{name:'201111',l:'20',c:'11',v:'11',texto:"Pela bênção dos sinceros, se exalta a cidade, mas pela boca dos ímpios é derribada." },
{name:'201112',l:'20',c:'11',v:'12',texto:"O que despreza o seu próximo é falto de sabedoria, mas o homem de entendimento cala-se." },
{name:'201113',l:'20',c:'11',v:'13',texto:"O que anda praguejando descobre o segredo, mas o fiel de espírito encobre o negócio." },
{name:'201114',l:'20',c:'11',v:'14',texto:"Não havendo sábia direção, o povo cai, mas, na multidão de conselheiros, há segurança." },
{name:'201115',l:'20',c:'11',v:'15',texto:"Decerto sofrerá severamente aquele que fica por fiador do estranho, mas o que aborrece a fiança estará seguro." },
{name:'201116',l:'20',c:'11',v:'16',texto:"A mulher aprazível guarda a honra, como os violentos guardam as riquezas." },
{name:'201117',l:'20',c:'11',v:'17',texto:"O homem benigno faz bem à sua própria alma, mas o cruel perturba a sua própria carne." },
{name:'201118',l:'20',c:'11',v:'18',texto:"O ímpio recebe um salário enganoso, mas, para o que semeia justiça, haverá galardão certo." },
{name:'201119',l:'20',c:'11',v:'19',texto:"Como a justiça encaminha para a vida, assim o que segue o mal faz isso para sua morte." },
{name:'201120',l:'20',c:'11',v:'20',texto:"Abominação para o SENHOR são os perversos de coração, mas os que são perfeitos em seu caminho são o seu deleite." },
{name:'201121',l:'20',c:'11',v:'21',texto:"Ainda que o mau junte mão à mão, não ficará sem castigo, mas a semente dos justos escapará." },
{name:'201122',l:'20',c:'11',v:'22',texto:"Como jóia de ouro em focinho de porca, assim é a mulher formosa que se aparta da razão." },
{name:'201123',l:'20',c:'11',v:'23',texto:"O desejo dos justos é somente o bem, mas a esperança dos ímpios é a ira." },
{name:'201124',l:'20',c:'11',v:'24',texto:"Alguns há que espalham, e ainda se lhes acrescenta mais; e outros, que retêm mais do que é justo, mas é para a sua perda." },
{name:'201125',l:'20',c:'11',v:'25',texto:"A alma generosa engordará, e o que regar também será regado." },
{name:'201126',l:'20',c:'11',v:'26',texto:"Ao que retém o trigo o povo o amaldiçoa, mas bênção haverá sobre a cabeça do vendedor." },
{name:'201127',l:'20',c:'11',v:'27',texto:"O que busca cedo o bem busca favor, mas ao que procura o mal, este lhe sobrevirá." },
{name:'201128',l:'20',c:'11',v:'28',texto:"Aquele que confia nas suas riquezas cairá, mas os justos reverdecerão como a rama." },
{name:'201129',l:'20',c:'11',v:'29',texto:"O que perturba a sua casa herdará o vento, e o tolo será servo do sábio de coração." },
{name:'201130',l:'20',c:'11',v:'30',texto:"O fruto do justo é árvore de vida, e o que ganha almas sábio é." },
{name:'201131',l:'20',c:'11',v:'31',texto:"Eis que o justo é punido na terra; quanto mais o ímpio e o pecador!" },
],
numVersiculos: 31,
},

c12: {
versiculos:[
{name:'20121',l:'20',c:'12',v:'1',texto:"O que ama a correção ama o conhecimento, mas o que aborrece a repreensão é um bruto." },
{name:'20122',l:'20',c:'12',v:'2',texto:"O homem de bem alcançará o favor do SENHOR, mas ao homem de perversas imaginações ele condenará." },
{name:'20123',l:'20',c:'12',v:'3',texto:"O homem não se estabelecerá pela impiedade, mas a raiz dos justos não será removida." },
{name:'20124',l:'20',c:'12',v:'4',texto:"A mulher virtuosa é a coroa do seu marido, mas a que procede vergonhosamente é como apodrecimento nos seus ossos." },
{name:'20125',l:'20',c:'12',v:'5',texto:"Os pensamentos do justo são retos, mas os conselhos do ímpio, engano." },
{name:'20126',l:'20',c:'12',v:'6',texto:"As palavras dos ímpios são para armarem ciladas ao sangue, mas a boca dos retos os livrará." },
{name:'20127',l:'20',c:'12',v:'7',texto:"Transtornados serão os ímpios e não serão mais, mas a casa dos justos permanecerá." },
{name:'20128',l:'20',c:'12',v:'8',texto:"Segundo o seu entendimento, será louvado cada qual, mas o perverso de coração estará em desprezo." },
{name:'20129',l:'20',c:'12',v:'9',texto:"Melhor é o que se estima em pouco e tem servos do que o que se honra a si mesmo e tem falta de pão." },
{name:'201210',l:'20',c:'12',v:'10',texto:"O justo olha pela vida dos seus animais, mas as misericórdias dos ímpios são cruéis." },
{name:'201211',l:'20',c:'12',v:'11',texto:"O que lavra a sua terra se fartará de pão, mas o que segue os ociosos está falto de juízo." },
{name:'201212',l:'20',c:'12',v:'12',texto:"Deseja o ímpio a rede dos maus, mas a raiz dos justos produz o seu fruto." },
{name:'201213',l:'20',c:'12',v:'13',texto:"O laço do ímpio está na transgressão dos lábios, mas o justo sairá da angústia." },
{name:'201214',l:'20',c:'12',v:'14',texto:"Cada um se farta de bem pelo fruto da sua boca, e o que as mãos do homem fizerem isso ele receberá." },
{name:'201215',l:'20',c:'12',v:'15',texto:"O caminho do tolo é reto aos seus olhos, mas o que dá ouvidos ao conselho é sábio." },
{name:'201216',l:'20',c:'12',v:'16',texto:"A ira do louco se conhece no mesmo dia, mas o avisado encobre a afronta." },
{name:'201217',l:'20',c:'12',v:'17',texto:"O que diz a verdade manifesta a justiça, mas a testemunha falsa engana." },
{name:'201218',l:'20',c:'12',v:'18',texto:"Há alguns cujas palavras são como pontas de espada, mas a língua dos sábios é saúde." },
{name:'201219',l:'20',c:'12',v:'19',texto:"O lábio de verdade ficará para sempre, mas a língua mentirosa dura só um momento." },
{name:'201220',l:'20',c:'12',v:'20',texto:"Engano há no coração dos que maquinam mal, mas alegria têm os que aconselham a paz." },
{name:'201221',l:'20',c:'12',v:'21',texto:"Nenhum agravo sobrevirá ao justo, mas os ímpios ficam cheios de mal." },
{name:'201222',l:'20',c:'12',v:'22',texto:"Os lábios mentirosos são abomináveis ao SENHOR, mas os que agem fielmente são o seu deleite." },
{name:'201223',l:'20',c:'12',v:'23',texto:"O homem avisado encobre o conhecimento, mas o coração dos tolos proclama a estultícia." },
{name:'201224',l:'20',c:'12',v:'24',texto:"A mão dos diligentes dominará, mas os enganadores serão tributários." },
{name:'201225',l:'20',c:'12',v:'25',texto:"A solicitude no coração do homem o abate, mas uma boa palavra o alegra." },
{name:'201226',l:'20',c:'12',v:'26',texto:"O justo é um guia para o seu companheiro, mas o caminho dos ímpios os faz errar." },
{name:'201227',l:'20',c:'12',v:'27',texto:"O preguiçoso não assará a sua caça, mas o bem precioso do homem é ser diligente." },
{name:'201228',l:'20',c:'12',v:'28',texto:"Na vereda da justiça está a vida, e no caminho da sua carreira não há morte." },
],
numVersiculos: 28,
},

c13: {
versiculos:[
{name:'20131',l:'20',c:'13',v:'1',texto:"O filho sábio ouve a correção do pai, mas o escarnecedor não ouve a repreensão." },
{name:'20132',l:'20',c:'13',v:'2',texto:"Do fruto da boca cada um comerá o bem, mas a alma dos prevaricadores comerá a violência." },
{name:'20133',l:'20',c:'13',v:'3',texto:"O que guarda a sua boca conserva a sua alma, mas o que muito abre os lábios tem perturbação." },
{name:'20134',l:'20',c:'13',v:'4',texto:"A alma do preguiçoso deseja e coisa nenhuma alcança, mas a alma dos diligentes engorda." },
{name:'20135',l:'20',c:'13',v:'5',texto:"O justo aborrece a palavra de mentira, mas o ímpio é abominável e se confunde." },
{name:'20136',l:'20',c:'13',v:'6',texto:"A justiça guarda ao que é sincero no seu caminho, mas a impiedade transtornará o pecador." },
{name:'20137',l:'20',c:'13',v:'7',texto:"Há quem se faça rico, não tendo coisa nenhuma, e quem se faça pobre, tendo grande riqueza." },
{name:'20138',l:'20',c:'13',v:'8',texto:"O resgate da vida de cada um são as suas riquezas, mas o pobre não ouve as ameaças." },
{name:'20139',l:'20',c:'13',v:'9',texto:"A luz dos justos alegra, mas a candeia dos ímpios se apagará." },
{name:'201310',l:'20',c:'13',v:'10',texto:"Da soberba só provém a contenda, mas com os que se aconselham se acha a sabedoria." },
{name:'201311',l:'20',c:'13',v:'11',texto:"A fazenda que procede da vaidade diminuirá, mas quem a ajunta pelo trabalho terá aumento." },
{name:'201312',l:'20',c:'13',v:'12',texto:"A esperança demorada enfraquece o coração, mas o desejo chegado é árvore de vida." },
{name:'201313',l:'20',c:'13',v:'13',texto:"O que despreza a palavra perecerá, mas o que teme o mandamento será galardoado." },
{name:'201314',l:'20',c:'13',v:'14',texto:"A doutrina do sábio é uma fonte de vida para desviar dos laços da morte." },
{name:'201315',l:'20',c:'13',v:'15',texto:"O bom entendimento dá graça, mas o caminho dos prevaricadores é áspero." },
{name:'201316',l:'20',c:'13',v:'16',texto:"Todo prudente age com conhecimento, mas o tolo espraia a sua loucura." },
{name:'201317',l:'20',c:'13',v:'17',texto:"Um mau mensageiro cai no mal, mas o embaixador fiel é saúde." },
{name:'201318',l:'20',c:'13',v:'18',texto:"Pobreza e afronta virão ao que rejeita a correção, mas o que guarda a repreensão será venerado." },
{name:'201319',l:'20',c:'13',v:'19',texto:"O desejo que se cumpre deleita a alma, mas apartar-se do mal é abominação para os loucos." },
{name:'201320',l:'20',c:'13',v:'20',texto:"Anda com os sábios e serás sábio, mas o companheiro dos tolos será afligido." },
{name:'201321',l:'20',c:'13',v:'21',texto:"O mal perseguirá aos pecadores, mas os justos serão galardoados com o bem." },
{name:'201322',l:'20',c:'13',v:'22',texto:"O homem de bem deixa uma herança aos filhos de seus filhos, mas a riqueza do pecador é depositada para o justo." },
{name:'201323',l:'20',c:'13',v:'23',texto:"Abundância de mantimento há na lavoura do pobre, mas alguns há que se consomem por falta de juízo." },
{name:'201324',l:'20',c:'13',v:'24',texto:"O que retém a sua vara aborrece a seu filho, mas o que o ama, a seu tempo, o castiga." },
{name:'201325',l:'20',c:'13',v:'25',texto:"O justo come até que a sua alma fique satisfeita, mas o ventre dos ímpios terá necessidade." },
],
numVersiculos: 25,
},

c14: {
versiculos:[
{name:'20141',l:'20',c:'14',v:'1',texto:"Toda mulher sábia edifica a sua casa, mas a tola derriba-a com as suas mãos." },
{name:'20142',l:'20',c:'14',v:'2',texto:"O que anda na sua sinceridade teme ao SENHOR, mas o que se desvia de seus caminhos despreza-o." },
{name:'20143',l:'20',c:'14',v:'3',texto:"Na boca do tolo está a vara da soberba, mas os lábios do sábio preservá-lo-ão." },
{name:'20144',l:'20',c:'14',v:'4',texto:"Não havendo bois, o celeiro fica limpo, mas, pela força do boi, há abundância de colheitas." },
{name:'20145',l:'20',c:'14',v:'5',texto:"A testemunha verdadeira não mentirá, mas a testemunha falsa se desboca em mentiras." },
{name:'20146',l:'20',c:'14',v:'6',texto:"O escarnecedor busca sabedoria e não a acha, mas para o prudente o conhecimento é fácil." },
{name:'20147',l:'20',c:'14',v:'7',texto:"Vai-te à presença do homem insensato e nele não divisarás os lábios do conhecimento." },
{name:'20148',l:'20',c:'14',v:'8',texto:"A sabedoria do prudente é entender o seu caminho, mas a estultícia dos tolos é enganar." },
{name:'20149',l:'20',c:'14',v:'9',texto:"Os loucos zombam do pecado, mas entre os retos há boa vontade." },
{name:'201410',l:'20',c:'14',v:'10',texto:"O coração conhece a sua própria amargura, e o estranho não se entremeterá na sua alegria." },
{name:'201411',l:'20',c:'14',v:'11',texto:"A casa dos ímpios se desfará, mas a tenda dos retos florescerá." },
{name:'201412',l:'20',c:'14',v:'12',texto:"Há caminho que ao homem parece direito, mas o fim dele são os caminhos da morte." },
{name:'201413',l:'20',c:'14',v:'13',texto:"Até no riso terá dor o coração, e o fim da alegria é tristeza." },
{name:'201414',l:'20',c:'14',v:'14',texto:"Dos seus caminhos se fartará o infiel de coração, mas o homem bom se fartará de si mesmo." },
{name:'201415',l:'20',c:'14',v:'15',texto:"O simples dá crédito a cada palavra, mas o prudente atenta para os seus passos." },
{name:'201416',l:'20',c:'14',v:'16',texto:"O sábio teme e desvia-se do mal, mas o tolo encoleriza-se e dá-se por seguro." },
{name:'201417',l:'20',c:'14',v:'17',texto:"O que presto se ira fará doidices, e o homem de más imaginações será aborrecido." },
{name:'201418',l:'20',c:'14',v:'18',texto:"Os simples herdarão a estultícia, mas os prudentes se coroarão de conhecimento." },
{name:'201419',l:'20',c:'14',v:'19',texto:"Os maus inclinam-se perante a face dos bons, e os ímpios, diante das portas do justo." },
{name:'201420',l:'20',c:'14',v:'20',texto:"O pobre é aborrecido até do companheiro, mas os amigos dos ricos são muitos." },
{name:'201421',l:'20',c:'14',v:'21',texto:"O que despreza ao seu companheiro peca, mas o que se compadece dos humildes é bem-aventurado." },
{name:'201422',l:'20',c:'14',v:'22',texto:"Porventura, não erram os que praticam o mal? Mas beneficência e fidelidade haverá para os que praticam o bem." },
{name:'201423',l:'20',c:'14',v:'23',texto:"Em todo trabalho há proveito, mas a palavra dos lábios só encaminha para a pobreza." },
{name:'201424',l:'20',c:'14',v:'24',texto:"A coroa dos sábios é a sua riqueza, a estultícia dos tolos é só estultícia." },
{name:'201425',l:'20',c:'14',v:'25',texto:"A testemunha verdadeira livra as almas, mas o que se desboca em mentiras é enganador." },
{name:'201426',l:'20',c:'14',v:'26',texto:"No temor do SENHOR, há firme confiança, e ele será um refúgio para seus filhos." },
{name:'201427',l:'20',c:'14',v:'27',texto:"O temor do SENHOR é uma fonte de vida para preservar dos laços da morte." },
{name:'201428',l:'20',c:'14',v:'28',texto:"Na multidão do povo está a magnificência do rei, mas, na falta de povo, a perturbação do príncipe." },
{name:'201429',l:'20',c:'14',v:'29',texto:"O longânimo é grande em entendimento, mas o de ânimo precipitado exalta a loucura." },
{name:'201430',l:'20',c:'14',v:'30',texto:"O coração com saúde é a vida da carne, mas a inveja é a podridão dos ossos." },
{name:'201431',l:'20',c:'14',v:'31',texto:"O que oprime ao pobre insulta aquele que o criou, mas o que se compadece do necessitado honra-o." },
{name:'201432',l:'20',c:'14',v:'32',texto:"Pela sua malícia, será lançado fora o ímpio, mas o justo até na sua morte tem esperança." },
{name:'201433',l:'20',c:'14',v:'33',texto:"No coração do prudente, repousa a sabedoria, mas o que há no interior dos tolos se conhece." },
{name:'201434',l:'20',c:'14',v:'34',texto:"A justiça exalta as nações, mas o pecado é o opróbrio dos povos." },
{name:'201435',l:'20',c:'14',v:'35',texto:"O rei tem seu contentamento no servo prudente, mas, sobre o que procede indignamente, cairá o seu furor." },
],
numVersiculos: 35,
},

c15: {
versiculos:[
{name:'20151',l:'20',c:'15',v:'1',texto:"A resposta branda desvia o furor, mas a palavra dura suscita a ira." },
{name:'20152',l:'20',c:'15',v:'2',texto:"A língua dos sábios adorna a sabedoria, mas a boca dos tolos derrama a estultícia." },
{name:'20153',l:'20',c:'15',v:'3',texto:"Os olhos do SENHOR estão em todo lugar, contemplando os maus e os bons." },
{name:'20154',l:'20',c:'15',v:'4',texto:"Uma língua saudável é árvore de vida, mas a perversidade nela quebranta o espírito." },
{name:'20155',l:'20',c:'15',v:'5',texto:"O tolo despreza a correção de seu pai, mas o que observa a repreensão prudentemente se haverá." },
{name:'20156',l:'20',c:'15',v:'6',texto:"Na casa do justo há um grande tesouro, mas nos frutos do ímpio há perturbação." },
{name:'20157',l:'20',c:'15',v:'7',texto:"Os lábios dos sábios derramarão o conhecimento, mas o coração dos tolos não fará assim." },
{name:'20158',l:'20',c:'15',v:'8',texto:"O sacrifício dos ímpios é abominável ao SENHOR, mas a oração dos retos é o seu contentamento." },
{name:'20159',l:'20',c:'15',v:'9',texto:"O caminho do ímpio é abominável ao SENHOR, mas ele ama o que segue a justiça." },
{name:'201510',l:'20',c:'15',v:'10',texto:"Correção molesta há para o que deixa a vereda, e o que aborrece a repreensão morrerá." },
{name:'201511',l:'20',c:'15',v:'11',texto:"O inferno e a perdição estão perante o SENHOR; quanto mais o coração dos filhos dos homens!" },
{name:'201512',l:'20',c:'15',v:'12',texto:"Não ama o escarnecedor aquele que o repreende, nem se chegará para os sábios." },
{name:'201513',l:'20',c:'15',v:'13',texto:"O coração alegre aformoseia o rosto, mas, pela dor do coração, o espírito se abate." },
{name:'201514',l:'20',c:'15',v:'14',texto:"O coração sábio buscará o conhecimento, mas a boca dos tolos se apascentará de estultícia." },
{name:'201515',l:'20',c:'15',v:'15',texto:"Todos os dias do aflito são maus, mas o de coração alegre tem um banquete contínuo." },
{name:'201516',l:'20',c:'15',v:'16',texto:"Melhor é o pouco com o temor do SENHOR do que um grande tesouro onde há inquietação." },
{name:'201517',l:'20',c:'15',v:'17',texto:"Melhor é a comida de hortaliça onde há amor do que o boi gordo e, com ele, o ódio." },
{name:'201518',l:'20',c:'15',v:'18',texto:"O homem iracundo suscita contendas, mas o longânimo apaziguará a luta." },
{name:'201519',l:'20',c:'15',v:'19',texto:"O caminho do preguiçoso é como a sebe de espinhos, mas a vereda dos retos está bem igualada." },
{name:'201520',l:'20',c:'15',v:'20',texto:"O filho sábio alegrará a seu pai, mas o homem insensato despreza a sua mãe." },
{name:'201521',l:'20',c:'15',v:'21',texto:"A estultícia é alegria para o que carece de entendimento, mas o homem sábio anda retamente." },
{name:'201522',l:'20',c:'15',v:'22',texto:"Onde não há conselho os projetos saem vãos, mas, com a multidão de conselheiros, se confirmarão." },
{name:'201523',l:'20',c:'15',v:'23',texto:"O homem se alegra na resposta da sua boca, e a palavra, a seu tempo, quão boa é!" },
{name:'201524',l:'20',c:'15',v:'24',texto:"Para o sábio, o caminho da vida é para cima, para que ele se desvie do inferno que está embaixo." },
{name:'201525',l:'20',c:'15',v:'25',texto:"O SENHOR arrancará a casa dos soberbos, mas firmará a herança da viúva." },
{name:'201526',l:'20',c:'15',v:'26',texto:"Abomináveis são para o SENHOR os pensamentos do mau, mas as palavras dos limpos são aprazíveis." },
{name:'201527',l:'20',c:'15',v:'27',texto:"O que se dá à cobiça perturba a sua casa, mas o que aborrece as dádivas viverá." },
{name:'201528',l:'20',c:'15',v:'28',texto:"O coração do justo medita o que há de responder, mas a boca dos ímpios derrama em abundância coisas más." },
{name:'201529',l:'20',c:'15',v:'29',texto:"Longe está o SENHOR dos ímpios, mas escutará a oração dos justos." },
{name:'201530',l:'20',c:'15',v:'30',texto:"A luz dos olhos alegra o coração; a boa fama engorda os ossos." },
{name:'201531',l:'20',c:'15',v:'31',texto:"Os ouvidos que escutam a repreensão da vida no meio dos sábios farão a sua morada." },
{name:'201532',l:'20',c:'15',v:'32',texto:"O que rejeita a correção menospreza a sua alma, mas o que escuta a repreensão adquire entendimento." },
{name:'201533',l:'20',c:'15',v:'33',texto:"O temor do SENHOR é a instrução da sabedoria, e diante da honra vai a humildade." },
],
numVersiculos: 33,
},

c16: {
versiculos:[
{name:'20161',l:'20',c:'16',v:'1',texto:"Do homem são as preparações do coração, mas do SENHOR, a resposta da boca." },
{name:'20162',l:'20',c:'16',v:'2',texto:"Todos os caminhos do homem são limpos aos seus olhos, mas o SENHOR pesa os espíritos." },
{name:'20163',l:'20',c:'16',v:'3',texto:"Confia ao SENHOR as tuas obras, e teus pensamentos serão estabelecidos." },
{name:'20164',l:'20',c:'16',v:'4',texto:"O SENHOR fez todas as coisas para os seus próprios fins e até ao ímpio, para o dia do mal." },
{name:'20165',l:'20',c:'16',v:'5',texto:"Abominação é para o SENHOR todo altivo de coração; ainda que ele junte mão à mão, não ficará impune." },
{name:'20166',l:'20',c:'16',v:'6',texto:"Pela misericórdia e pela verdade, se purifica a iniqüidade; e, pelo temor do SENHOR, os homens se desviam do mal." },
{name:'20167',l:'20',c:'16',v:'7',texto:"Sendo os caminhos do homem agradáveis ao SENHOR, até a seus inimigos faz que tenham paz com ele." },
{name:'20168',l:'20',c:'16',v:'8',texto:"Melhor é o pouco com justiça do que a abundância de colheita com injustiça." },
{name:'20169',l:'20',c:'16',v:'9',texto:"O coração do homem considera o seu caminho, mas o SENHOR lhe dirige os passos." },
{name:'201610',l:'20',c:'16',v:'10',texto:"Adivinhação se acha nos lábios do rei; em juízo não prevaricará a sua boca." },
{name:'201611',l:'20',c:'16',v:'11',texto:"O peso e a balança justa são do SENHOR; obra sua são todas as pedras da bolsa." },
{name:'201612',l:'20',c:'16',v:'12',texto:"Abominação é para os reis o praticarem a impiedade, porque com justiça se estabelece o trono." },
{name:'201613',l:'20',c:'16',v:'13',texto:"Os lábios de justiça são o contentamento dos reis, e eles amarão o que fala coisas retas." },
{name:'201614',l:'20',c:'16',v:'14',texto:"O furor do rei é como um mensageiro da morte, mas o homem sábio o apaziguará." },
{name:'201615',l:'20',c:'16',v:'15',texto:"Na luz do rosto do rei está a vida, e a sua benevolência é como a nuvem de chuva serôdia." },
{name:'201616',l:'20',c:'16',v:'16',texto:"Quanto melhor é adquirir a sabedoria do que o ouro! E quanto mais excelente, adquirir a prudência do que a prata!" },
{name:'201617',l:'20',c:'16',v:'17',texto:"O alto caminho dos retos é desviar-se do mal; o que guarda o seu caminho preserva a sua alma." },
{name:'201618',l:'20',c:'16',v:'18',texto:"A soberba precede a ruína, e a altivez do espírito precede a queda." },
{name:'201619',l:'20',c:'16',v:'19',texto:"Melhor é ser humilde de espírito com os mansos do que repartir o despojo com os soberbos." },
{name:'201620',l:'20',c:'16',v:'20',texto:"O que atenta prudentemente para a palavra achará o bem, e o que confia no SENHOR será bem-aventurado." },
{name:'201621',l:'20',c:'16',v:'21',texto:"O sábio de coração será chamado prudente, e a doçura dos lábios aumentará o ensino." },
{name:'201622',l:'20',c:'16',v:'22',texto:"O entendimento, para aqueles que o possuem, é uma fonte de vida, mas a instrução dos tolos é a sua estultícia." },
{name:'201623',l:'20',c:'16',v:'23',texto:"O coração do sábio instrui a sua boca e acrescenta doutrina aos seus lábios." },
{name:'201624',l:'20',c:'16',v:'24',texto:"Favo de mel são as palavras suaves: doces para a alma e saúde para os ossos." },
{name:'201625',l:'20',c:'16',v:'25',texto:"Há caminho que parece direito ao homem, mas o seu fim são os caminhos da morte." },
{name:'201626',l:'20',c:'16',v:'26',texto:"O trabalhador trabalha para si mesmo, porque a sua boca o instiga." },
{name:'201627',l:'20',c:'16',v:'27',texto:"O homem vão cava o mal, e nos seus lábios se acha como que um fogo ardente." },
{name:'201628',l:'20',c:'16',v:'28',texto:"O homem perverso levanta a contenda, e o difamador separa os maiores amigos." },
{name:'201629',l:'20',c:'16',v:'29',texto:"O homem violento persuade o seu companheiro e guia-o por caminho não bom." },
{name:'201630',l:'20',c:'16',v:'30',texto:"Fecha os olhos para imaginar perversidades; mordendo os lábios, efetua o mal." },
{name:'201631',l:'20',c:'16',v:'31',texto:"Coroa de honra são as cãs, achando-se elas no caminho da justiça." },
{name:'201632',l:'20',c:'16',v:'32',texto:"Melhor é o longânimo do que o valente, e o que governa o seu espírito do que o que toma uma cidade." },
{name:'201633',l:'20',c:'16',v:'33',texto:"A sorte se lança no regaço, mas do SENHOR procede toda a sua disposição." },
],
numVersiculos: 33,
},

c17: {
versiculos:[
{name:'20171',l:'20',c:'17',v:'1',texto:"Melhor é um bocado seco e com ele a tranqüilidade do que a casa cheia de vítimas, com contenda." },
{name:'20172',l:'20',c:'17',v:'2',texto:"O servo prudente dominará sobre o filho que procede indignamente; e entre os irmãos repartirá a herança." },
{name:'20173',l:'20',c:'17',v:'3',texto:"O crisol é para a prata, e o forno, para o ouro; mas o SENHOR prova os corações." },
{name:'20174',l:'20',c:'17',v:'4',texto:"O malfazejo atenta para o lábio iníquo; o mentiroso inclina os ouvidos para a língua maligna." },
{name:'20175',l:'20',c:'17',v:'5',texto:"O que escarnece do pobre insulta ao que o criou; o que se alegra da calamidade não ficará impune." },
{name:'20176',l:'20',c:'17',v:'6',texto:"Coroa dos velhos são os filhos dos filhos; e a glória dos filhos são seus pais." },
{name:'20177',l:'20',c:'17',v:'7',texto:"Não convém ao tolo a fala excelente; quanto menos ao príncipe, o lábio mentiroso!" },
{name:'20178',l:'20',c:'17',v:'8',texto:"Pedra preciosa é o presente aos olhos dos que o recebem; para onde quer que se volte, servirá de proveito." },
{name:'20179',l:'20',c:'17',v:'9',texto:"O que encobre a transgressão busca a amizade, mas o que renova a questão separa os maiores amigos." },
{name:'201710',l:'20',c:'17',v:'10',texto:"Mais profundamente entra a repreensão no prudente do que cem açoites no tolo." },
{name:'201711',l:'20',c:'17',v:'11',texto:"Na verdade, o rebelde não busca senão o mal, mas mensageiro cruel se enviará contra ele." },
{name:'201712',l:'20',c:'17',v:'12',texto:"Encontre-se com o homem a ursa à qual roubaram os filhos, mas não o louco na sua estultícia." },
{name:'201713',l:'20',c:'17',v:'13',texto:"Quanto àquele que torna mal por bem, não se apartará o mal da sua casa." },
{name:'201714',l:'20',c:'17',v:'14',texto:"Como o soltar as águas, é o princípio da contenda; deixa por isso a porfia, antes que sejas envolvido." },
{name:'201715',l:'20',c:'17',v:'15',texto:"O que justifica o ímpio e o que condena o justo abomináveis são para o SENHOR, tanto um como o outro." },
{name:'201716',l:'20',c:'17',v:'16',texto:"De que serviria o preço na mão do tolo para comprar a sabedoria, visto que não tem entendimento?" },
{name:'201717',l:'20',c:'17',v:'17',texto:"Em todo o tempo ama o amigo; e na angústia nasce o irmão." },
{name:'201718',l:'20',c:'17',v:'18',texto:"O homem falto de entendimento dá a mão, ficando por fiador do seu companheiro." },
{name:'201719',l:'20',c:'17',v:'19',texto:"O que ama a contenda ama a transgressão; o que alça a sua porta busca a ruína." },
{name:'201720',l:'20',c:'17',v:'20',texto:"O perverso de coração nunca achará o bem; e o que tem a língua dobre virá a cair no mal." },
{name:'201721',l:'20',c:'17',v:'21',texto:"O que gera um tolo, para sua tristeza o faz; e o pai do insensato não se alegrará." },
{name:'201722',l:'20',c:'17',v:'22',texto:"O coração alegre serve de bom remédio, mas o espírito abatido virá a secar os ossos." },
{name:'201723',l:'20',c:'17',v:'23',texto:"O ímpio tira o presente do seio para perverter as veredas da justiça." },
{name:'201724',l:'20',c:'17',v:'24',texto:"No rosto do sábio se vê a sabedoria, mas os olhos do louco estão nas extremidades da terra." },
{name:'201725',l:'20',c:'17',v:'25',texto:"O filho insensato é tristeza para seu pai e amargura para quem o deu à luz." },
{name:'201726',l:'20',c:'17',v:'26',texto:"Não é bom também punir o justo, nem ferirem os príncipes ao que age justamente." },
{name:'201727',l:'20',c:'17',v:'27',texto:"Retém as suas palavras o que possui o conhecimento, e o homem de entendimento é de precioso espírito." },
{name:'201728',l:'20',c:'17',v:'28',texto:"Até o tolo, quando se cala, será reputado por sábio; e o que cerrar os seus lábios, por sábio." },
],
numVersiculos: 28,
},

c18: {
versiculos:[
{name:'20181',l:'20',c:'18',v:'1',texto:"Busca seu próprio desejo aquele que se separa; ele insurge-se contra a verdadeira sabedoria." },
{name:'20182',l:'20',c:'18',v:'2',texto:"Não toma prazer o tolo no entendimento, senão em que se descubra o seu coração." },
{name:'20183',l:'20',c:'18',v:'3',texto:"Vindo o ímpio, vem também o desprezo; e, com a ignomínia, a vergonha." },
{name:'20184',l:'20',c:'18',v:'4',texto:"Águas profundas são as palavras da boca do homem, e ribeiro transbordante é a fonte da sabedoria." },
{name:'20185',l:'20',c:'18',v:'5',texto:"Não é bom ter respeito à pessoa do ímpio, para derribar o justo em juízo." },
{name:'20186',l:'20',c:'18',v:'6',texto:"Os lábios do tolo entram na contenda, e a sua boca brada por açoites." },
{name:'20187',l:'20',c:'18',v:'7',texto:"A boca do tolo é a sua própria destruição, e os seus lábios, um laço para a sua alma." },
{name:'20188',l:'20',c:'18',v:'8',texto:"As palavras do linguareiro são como doces bocados, e elas descem ao íntimo do ventre." },
{name:'20189',l:'20',c:'18',v:'9',texto:"Também o negligente na sua obra é irmão do desperdiçador." },
{name:'201810',l:'20',c:'18',v:'10',texto:"Torre forte é o nome do SENHOR; para ela correrá o justo e estará em alto retiro." },
{name:'201811',l:'20',c:'18',v:'11',texto:"A fazenda do rico é sua cidade forte e, como um muro alto, na sua imaginação." },
{name:'201812',l:'20',c:'18',v:'12',texto:"Antes de ser quebrantado, eleva-se o coração do homem; e, diante da honra, vai a humildade." },
{name:'201813',l:'20',c:'18',v:'13',texto:"Responder antes de ouvir é estultícia e vergonha." },
{name:'201814',l:'20',c:'18',v:'14',texto:"O espírito do homem aliviará a sua enfermidade, mas ao espírito abatido, quem o levantará?" },
{name:'201815',l:'20',c:'18',v:'15',texto:"O coração do sábio adquire o conhecimento, e o ouvido dos sábios busca a ciência." },
{name:'201816',l:'20',c:'18',v:'16',texto:"O presente do homem alarga-lhe o caminho e leva-o à presença dos grandes." },
{name:'201817',l:'20',c:'18',v:'17',texto:"O que primeiro começa o seu pleito justo parece; mas vem o seu companheiro e o examina." },
{name:'201818',l:'20',c:'18',v:'18',texto:"A sorte faz cessar os pleitos e faz separação entre os poderosos." },
{name:'201819',l:'20',c:'18',v:'19',texto:"O irmão ofendido é mais difícil de conquistar do que uma cidade forte; e as contendas são como ferrolhos de um palácio." },
{name:'201820',l:'20',c:'18',v:'20',texto:"Do fruto da boca de cada um se fartará o seu ventre; dos renovos dos seus lábios se fartará." },
{name:'201821',l:'20',c:'18',v:'21',texto:"A morte e a vida estão no poder da língua; e aquele que a ama comerá do seu fruto." },
{name:'201822',l:'20',c:'18',v:'22',texto:"O que acha uma mulher acha uma coisa boa e alcançou a benevolência do SENHOR." },
{name:'201823',l:'20',c:'18',v:'23',texto:"O pobre fala com rogos, mas o rico responde com durezas." },
{name:'201824',l:'20',c:'18',v:'24',texto:"O homem que tem muitos amigos pode congratular-se, mas há amigo mais chegado do que um irmão." },
],
numVersiculos: 24,
},

c19: {
versiculos:[
{name:'20191',l:'20',c:'19',v:'1',texto:"Melhor é o pobre que anda na sua sinceridade do que o perverso de lábios e tolo." },
{name:'20192',l:'20',c:'19',v:'2',texto:"Assim também ficar a alma sem conhecimento não é bom; e o que se apressa com seus pés peca." },
{name:'20193',l:'20',c:'19',v:'3',texto:"A estultícia do homem perverterá o seu caminho, e o seu coração se irará contra o SENHOR." },
{name:'20194',l:'20',c:'19',v:'4',texto:"As riquezas granjeiam muitos amigos, mas ao pobre o seu próprio amigo o deixa." },
{name:'20195',l:'20',c:'19',v:'5',texto:"A falsa testemunha não ficará inocente; e o que profere mentiras não escapará." },
{name:'20196',l:'20',c:'19',v:'6',texto:"Muitos suplicam a face do príncipe, e cada um é amigo daquele que dá presentes." },
{name:'20197',l:'20',c:'19',v:'7',texto:"Todos os irmãos do pobre o aborrecem; quanto mais se afastarão dele os seus amigos! Corre após eles com palavras, mas não servem de nada." },
{name:'20198',l:'20',c:'19',v:'8',texto:"O que adquire entendimento ama a sua alma; o que conserva a inteligência achará o bem." },
{name:'20199',l:'20',c:'19',v:'9',texto:"A falsa testemunha não ficará impune; e o que profere mentiras perecerá." },
{name:'201910',l:'20',c:'19',v:'10',texto:"Ao tolo não está bem o deleite; quanto menos ao servo dominar os príncipes!" },
{name:'201911',l:'20',c:'19',v:'11',texto:"O entendimento do homem retém a sua ira; e sua glória é passar sobre a transgressão." },
{name:'201912',l:'20',c:'19',v:'12',texto:"Como o bramido do filho do leão é a indignação do rei; mas, como o orvalho sobre a erva, é a sua benevolência." },
{name:'201913',l:'20',c:'19',v:'13',texto:"Grande miséria é para o pai o filho insensato, e um gotejar contínuo, as contenções da mulher." },
{name:'201914',l:'20',c:'19',v:'14',texto:"A casa e a fazenda são a herança dos pais; mas do SENHOR vem a mulher prudente." },
{name:'201915',l:'20',c:'19',v:'15',texto:"A preguiça faz cair em profundo sono, e a alma enganadora padecerá fome." },
{name:'201916',l:'20',c:'19',v:'16',texto:"O que guardar o mandamento guardará a sua alma; mas o que desprezar os seus caminhos morrerá." },
{name:'201917',l:'20',c:'19',v:'17',texto:"Ao SENHOR empresta o que se compadece do pobre, e ele lhe pagará o seu benefício." },
{name:'201918',l:'20',c:'19',v:'18',texto:"Castiga teu filho enquanto há esperança, mas para o matar não alçarás a tua alma." },
{name:'201919',l:'20',c:'19',v:'19',texto:"Homem de grande ira tem de sofrer o dano; porque, se tu o livrares, virás ainda a fazê-lo novamente." },
{name:'201920',l:'20',c:'19',v:'20',texto:"Ouve o conselho e recebe a correção, para que sejas sábio nos teus últimos dias." },
{name:'201921',l:'20',c:'19',v:'21',texto:"Muitos propósitos há no coração do homem, mas o conselho do SENHOR permanecerá." },
{name:'201922',l:'20',c:'19',v:'22',texto:"O desejo do homem é a sua beneficência; mas o pobre é melhor do que o mentiroso." },
{name:'201923',l:'20',c:'19',v:'23',texto:"O temor do SENHOR encaminha para a vida; aquele que o tem ficará satisfeito, e não o visitará mal nenhum." },
{name:'201924',l:'20',c:'19',v:'24',texto:"O preguiçoso esconde a mão no seio; enfada-se de a levar à boca." },
{name:'201925',l:'20',c:'19',v:'25',texto:"Fere o escarnecedor, e o simples tomará aviso; repreende ao sábio, e aprenderá conhecimento." },
{name:'201926',l:'20',c:'19',v:'26',texto:"O que aflige a seu pai ou afugenta a sua mãe filho é que envergonha e desonra." },
{name:'201927',l:'20',c:'19',v:'27',texto:"Cessa, filho meu, ouvindo a instrução, de te desviares das palavras do conhecimento." },
{name:'201928',l:'20',c:'19',v:'28',texto:"A testemunha de Belial escarnece do juízo, e a boca dos ímpios engole a iniqüidade." },
{name:'201929',l:'20',c:'19',v:'29',texto:"Preparados estão os juízos para os escarnecedores e os açoites para as costas dos tolos." },
],
numVersiculos: 29,
},

c20: {
versiculos:[
{name:'20201',l:'20',c:'20',v:'1',texto:"O vinho é escarnecedor, e a bebida forte, alvoroçadora; e todo aquele que neles errar nunca será sábio." },
{name:'20202',l:'20',c:'20',v:'2',texto:"Como o bramido do leão é o terror do rei; o que provoca a sua ira peca contra a sua própria alma." },
{name:'20203',l:'20',c:'20',v:'3',texto:"Honroso é para o homem o desviar-se de questões, mas todo tolo se entremete nelas." },
{name:'20204',l:'20',c:'20',v:'4',texto:"O preguiçoso não lavrará por causa do inverno, pelo que mendigará na sega e nada receberá." },
{name:'20205',l:'20',c:'20',v:'5',texto:"Como águas profundas é o conselho no coração do homem; mas o homem de inteligência o tirará para fora." },
{name:'20206',l:'20',c:'20',v:'6',texto:"Cada qual entre os homens apregoa a sua bondade; mas o homem fiel, quem o achará?" },
{name:'20207',l:'20',c:'20',v:'7',texto:"O justo anda na sua sinceridade; bem-aventurados serão os seus filhos depois dele." },
{name:'20208',l:'20',c:'20',v:'8',texto:"Assentando-se o rei no trono do juízo, com os seus olhos dissipa todo mal." },
{name:'20209',l:'20',c:'20',v:'9',texto:"Quem poderá dizer: Purifiquei o meu coração, limpo estou de meu pecado!" },
{name:'202010',l:'20',c:'20',v:'10',texto:"Duas espécies de peso e duas espécies de medida são abominação para o SENHOR, tanto uma coisa como outra." },
{name:'202011',l:'20',c:'20',v:'11',texto:"Até a criança se dará a conhecer pelas suas ações, se a sua obra for pura e reta." },
{name:'202012',l:'20',c:'20',v:'12',texto:"O ouvido que ouve e o olho que vê, o SENHOR os fez a ambos." },
{name:'202013',l:'20',c:'20',v:'13',texto:"Não ames o sono, para que não empobreças; abre os teus olhos e te fartarás de pão." },
{name:'202014',l:'20',c:'20',v:'14',texto:"Nada vale, nada vale, dirá o comprador, mas, indo-se, então, se gabará." },
{name:'202015',l:'20',c:'20',v:'15',texto:"Há ouro e abundância de rubins, mas os lábios do conhecimento são jóia preciosa." },
{name:'202016',l:'20',c:'20',v:'16',texto:"Aquele que fica por fiador do estranho tira a sua roupa e penhora-a por um estranho." },
{name:'202017',l:'20',c:'20',v:'17',texto:"Suave é ao homem o pão da mentira, mas, depois, a sua boca se encherá de pedrinhas de areia." },
{name:'202018',l:'20',c:'20',v:'18',texto:"Cada pensamento com conselho se confirma; e com conselhos prudentes faz a guerra." },
{name:'202019',l:'20',c:'20',v:'19',texto:"O que anda maldizendo descobre o segredo; pelo que, com o que afaga com seus lábios, não te entremetas." },
{name:'202020',l:'20',c:'20',v:'20',texto:"O que a seu pai ou a sua mãe amaldiçoar, apagar-se-lhe-á a sua lâmpada e ficará em trevas densas." },
{name:'202021',l:'20',c:'20',v:'21',texto:"Entrando-se apressadamente de posse de uma herança no princípio, o seu fim não será bendito." },
{name:'202022',l:'20',c:'20',v:'22',texto:"Não digas: Vingar-me-ei do mal; espera pelo SENHOR, e ele te livrará." },
{name:'202023',l:'20',c:'20',v:'23',texto:"Duas espécies de peso são abomináveis ao SENHOR, e balanças enganosas não são boas." },
{name:'202024',l:'20',c:'20',v:'24',texto:"Os passos do homem são dirigidos pelo SENHOR; o homem, pois, como entenderá o seu caminho?" },
{name:'202025',l:'20',c:'20',v:'25',texto:"Laço é para o homem dizer precipitadamente: É santo; e, feitos os votos, então, inquirir." },
{name:'202026',l:'20',c:'20',v:'26',texto:"O rei sábio dissipa os ímpios e faz girar sobre eles a roda." },
{name:'202027',l:'20',c:'20',v:'27',texto:"A alma do homem é a lâmpada do SENHOR, a qual esquadrinha todo o mais íntimo do ventre." },
{name:'202028',l:'20',c:'20',v:'28',texto:"Benignidade e verdade guardam o rei, e com benignidade sustém ele o seu trono." },
{name:'202029',l:'20',c:'20',v:'29',texto:"O ornato dos jovens é a sua força; e a beleza dos velhos, as cãs." },
{name:'202030',l:'20',c:'20',v:'30',texto:"Os vergões das feridas são a purificação dos maus, como também as pancadas que penetram até o mais íntimo do ventre." },
],
numVersiculos: 30,
},

c21: {
versiculos:[
{name:'20211',l:'20',c:'21',v:'1',texto:"Como ribeiros de águas, assim é o coração do rei na mão do SENHOR; a tudo quanto quer o inclina." },
{name:'20212',l:'20',c:'21',v:'2',texto:"Todo caminho do homem é reto aos seus olhos, mas o SENHOR sonda os corações." },
{name:'20213',l:'20',c:'21',v:'3',texto:"Fazer justiça e julgar com retidão é mais aceitável ao SENHOR do que oferecer-lhe sacrifício." },
{name:'20214',l:'20',c:'21',v:'4',texto:"Olhar altivo, coração orgulhoso e até a lavoura dos ímpios são pecado." },
{name:'20215',l:'20',c:'21',v:'5',texto:"Os pensamentos do diligente tendem à abundância, mas os de todo apressado, tão-somente à pobreza." },
{name:'20216',l:'20',c:'21',v:'6',texto:"Trabalhar por ajuntar tesouro com língua falsa é uma vaidade, e aqueles que a isso são impelidos buscam a morte." },
{name:'20217',l:'20',c:'21',v:'7',texto:"As rapinas dos ímpios virão a destruí-los, porquanto eles recusam praticar a justiça." },
{name:'20218',l:'20',c:'21',v:'8',texto:"O caminho do homem perverso é inteiramente tortuoso, mas a obra do puro é reta." },
{name:'20219',l:'20',c:'21',v:'9',texto:"Melhor é morar num canto de umas águas-furtadas do que com a mulher rixosa numa casa ampla." },
{name:'202110',l:'20',c:'21',v:'10',texto:"A alma do ímpio deseja o mal; o seu próximo não agrada aos seus olhos." },
{name:'202111',l:'20',c:'21',v:'11',texto:"Quando o escarnecedor é castigado, o simples torna-se sábio; e, quando o sábio é instruído, recebe o conhecimento." },
{name:'202112',l:'20',c:'21',v:'12',texto:"Prudentemente considera o justo a casa do ímpio, quando os ímpios são arrastados para o mal." },
{name:'202113',l:'20',c:'21',v:'13',texto:"O que tapa o seu ouvido ao clamor do pobre também clamará e não será ouvido." },
{name:'202114',l:'20',c:'21',v:'14',texto:"O presente que se dá em segredo abate a ira, e a dádiva no seio, uma forte indignação." },
{name:'202115',l:'20',c:'21',v:'15',texto:"Praticar a justiça é alegria para o justo, mas espanto para os que praticam a iniqüidade." },
{name:'202116',l:'20',c:'21',v:'16',texto:"O homem que anda desviado do caminho do entendimento na congregação dos mortos repousará." },
{name:'202117',l:'20',c:'21',v:'17',texto:"Necessidade padecerá o que ama os prazeres; o que ama o vinho e o azeite nunca enriquecerá." },
{name:'202118',l:'20',c:'21',v:'18',texto:"O resgate do justo é o ímpio; o do reto, o iníquo." },
{name:'202119',l:'20',c:'21',v:'19',texto:"Melhor é morar numa terra deserta do que com a mulher rixosa e iracunda." },
{name:'202120',l:'20',c:'21',v:'20',texto:"Tesouro desejável e azeite há na casa do sábio, mas o homem insensato o devora." },
{name:'202121',l:'20',c:'21',v:'21',texto:"O que segue a justiça e a bondade achará a vida, a justiça e a honra." },
{name:'202122',l:'20',c:'21',v:'22',texto:"À cidade dos fortes sobe o sábio e derruba a força em que confiaram." },
{name:'202123',l:'20',c:'21',v:'23',texto:"O que guarda a boca e a língua guarda das angústias a sua alma." },
{name:'202124',l:'20',c:'21',v:'24',texto:"Quanto ao soberbo e presumido, zombador é seu nome; trata com indignação e soberba." },
{name:'202125',l:'20',c:'21',v:'25',texto:"O desejo do preguiçoso o mata, porque as suas mãos recusam-se a trabalhar." },
{name:'202126',l:'20',c:'21',v:'26',texto:"Todo o dia avidamente cobiça, mas o justo dá e nada retém." },
{name:'202127',l:'20',c:'21',v:'27',texto:"O sacrifício dos ímpios é abominação; quanto mais oferecendo-o com intenção maligna!" },
{name:'202128',l:'20',c:'21',v:'28',texto:"A testemunha mentirosa perecerá, mas o homem que ouve falará sem imputação." },
{name:'202129',l:'20',c:'21',v:'29',texto:"O homem ímpio endurece o seu rosto, mas o reto considera o seu caminho." },
{name:'202130',l:'20',c:'21',v:'30',texto:"Não há sabedoria, nem inteligência, nem conselho contra o SENHOR." },
{name:'202131',l:'20',c:'21',v:'31',texto:"O cavalo prepara-se para o dia da batalha, mas do SENHOR vem a vitória." },
],
numVersiculos: 31,
},

c22: {
versiculos:[
{name:'20221',l:'20',c:'22',v:'1',texto:"Mais digno de ser escolhido é o bom nome do que as muitas riquezas; e a graça é melhor do que a riqueza e o ouro." },
{name:'20222',l:'20',c:'22',v:'2',texto:"O rico e o pobre se encontraram; a todos os fez o SENHOR." },
{name:'20223',l:'20',c:'22',v:'3',texto:"O avisado vê o mal e esconde-se; mas os simples passam e sofrem a pena." },
{name:'20224',l:'20',c:'22',v:'4',texto:"O galardão da humildade e o temor do SENHOR são riquezas, e honra, e vida." },
{name:'20225',l:'20',c:'22',v:'5',texto:"Espinhos e laços há no caminho do perverso; o que guarda a sua alma retira-se para longe dele." },
{name:'20226',l:'20',c:'22',v:'6',texto:"Instrui o menino no caminho em que deve andar, e, até quando envelhecer, não se desviará dele." },
{name:'20227',l:'20',c:'22',v:'7',texto:"O rico domina sobre os pobres, e o que toma emprestado é servo do que empresta." },
{name:'20228',l:'20',c:'22',v:'8',texto:"O que semear a perversidade segará males; e a vara da sua indignação falhará." },
{name:'20229',l:'20',c:'22',v:'9',texto:"O que é de bons olhos será abençoado, porque deu do seu pão ao pobre." },
{name:'202210',l:'20',c:'22',v:'10',texto:"Lança fora ao escarnecedor, e se irá a contenda; e cessará a questão e a vergonha." },
{name:'202211',l:'20',c:'22',v:'11',texto:"O que ama a pureza do coração e tem graça nos seus lábios terá por seu amigo o rei." },
{name:'202212',l:'20',c:'22',v:'12',texto:"Os olhos do SENHOR conservam o que tem conhecimento, mas as palavras do iníquo ele transtornará." },
{name:'202213',l:'20',c:'22',v:'13',texto:"Diz o preguiçoso: Um leão está lá fora; serei morto no meio das ruas." },
{name:'202214',l:'20',c:'22',v:'14',texto:"Cova profunda é a boca das mulheres estranhas; aquele contra quem o SENHOR se irar cairá nela." },
{name:'202215',l:'20',c:'22',v:'15',texto:"A estultícia está ligada ao coração do menino, mas a vara da correção a afugentará dele." },
{name:'202216',l:'20',c:'22',v:'16',texto:"O que oprime o pobre para se engrandecer a si ou o que dá ao rico, certamente, empobrecerá." },
{name:'202217',l:'20',c:'22',v:'17',texto:"Inclina o teu ouvido, e ouve as palavras dos sábios, e aplica o teu coração à minha ciência." },
{name:'202218',l:'20',c:'22',v:'18',texto:"Porque é coisa suave, se as guardares no teu coração, se as aplicares todas aos teus lábios." },
{name:'202219',l:'20',c:'22',v:'19',texto:"Para que a tua confiança esteja no SENHOR, a ti tas faço saber hoje, sim, a ti mesmo." },
{name:'202220',l:'20',c:'22',v:'20',texto:"Porventura, não te escrevi excelentes coisas acerca de todo conselho e conhecimento," },
{name:'202221',l:'20',c:'22',v:'21',texto:"para te fazer saber a certeza das palavras de verdade, para que possas responder palavras de verdade aos que te enviarem?" },
{name:'202222',l:'20',c:'22',v:'22',texto:"Não roubes ao pobre, porque é pobre, nem atropeles, na porta, o aflito." },
{name:'202223',l:'20',c:'22',v:'23',texto:"Porque o SENHOR defenderá a sua causa em juízo, e aos que os roubam lhes tirará a vida." },
{name:'202224',l:'20',c:'22',v:'24',texto:"Não acompanhes o iracundo, nem andes com o homem colérico," },
{name:'202225',l:'20',c:'22',v:'25',texto:"para que não aprendas as suas veredas e tomes um laço para a tua alma." },
{name:'202226',l:'20',c:'22',v:'26',texto:"Não estejas entre os que dão as mãos e entre os que ficam por fiadores de dívidas." },
{name:'202227',l:'20',c:'22',v:'27',texto:"Se não tens com que pagar, por que tirariam a tua cama de debaixo de ti?" },
{name:'202228',l:'20',c:'22',v:'28',texto:"Não removas os limites antigos que fizeram teus pais." },
{name:'202229',l:'20',c:'22',v:'29',texto:"Viste um homem diligente na sua obra? Perante reis será posto; não será posto perante os de baixa sorte." },
],
numVersiculos: 29,
},

c23: {
versiculos:[
{name:'20231',l:'20',c:'23',v:'1',texto:"Quando te assentares a comer com um governador, atenta bem para o que se te pôs diante;" },
{name:'20232',l:'20',c:'23',v:'2',texto:"e põe uma faca à tua garganta, se és homem glutão." },
{name:'20233',l:'20',c:'23',v:'3',texto:"Não cobices os seus manjares gostosos, porque são pão de mentiras." },
{name:'20234',l:'20',c:'23',v:'4',texto:"Não te canses para enriqueceres; dá de mão à tua própria sabedoria." },
{name:'20235',l:'20',c:'23',v:'5',texto:"Porventura, fitarás os olhos naquilo que não é nada? Porque, certamente, isso se fará asas e voará ao céu como a águia." },
{name:'20236',l:'20',c:'23',v:'6',texto:"Não comas o pão daquele que tem os olhos malignos, nem cobices os seus manjares gostosos." },
{name:'20237',l:'20',c:'23',v:'7',texto:"Porque, como imaginou na sua alma, assim é; ele te dirá: Come e bebe; mas o seu coração não estará contigo." },
{name:'20238',l:'20',c:'23',v:'8',texto:"Vomitarias o bocado que comeste e perderias as tuas suaves palavras." },
{name:'20239',l:'20',c:'23',v:'9',texto:"Não fales aos ouvidos do tolo, porque desprezará a sabedoria das tuas palavras." },
{name:'202310',l:'20',c:'23',v:'10',texto:"Não removas os limites antigos, nem entres nas herdades dos órfãos," },
{name:'202311',l:'20',c:'23',v:'11',texto:"porque o seu Redentor é forte; ele pleiteará a sua causa contra ti." },
{name:'202312',l:'20',c:'23',v:'12',texto:"Aplica à disciplina o teu coração e os teus ouvidos, às palavras do conhecimento." },
{name:'202313',l:'20',c:'23',v:'13',texto:"Não retires a disciplina da criança, porque, fustigando-a com a vara, nem por isso morrerá." },
{name:'202314',l:'20',c:'23',v:'14',texto:"Tu a fustigarás com a vara e livrarás a sua alma do inferno." },
{name:'202315',l:'20',c:'23',v:'15',texto:"Filho meu, se o teu coração for sábio, alegrar-se-á o meu coração, sim, o meu próprio." },
{name:'202316',l:'20',c:'23',v:'16',texto:"E exultará o meu íntimo, quando os teus lábios falarem coisas retas." },
{name:'202317',l:'20',c:'23',v:'17',texto:"Não tenha o teu coração inveja dos pecadores; antes, sê no temor do SENHOR todo o dia." },
{name:'202318',l:'20',c:'23',v:'18',texto:"Porque deveras há um fim bom; não será malograda a tua esperança." },
{name:'202319',l:'20',c:'23',v:'19',texto:"Ouve tu, filho meu, e sê sábio e dirige no caminho o teu coração." },
{name:'202320',l:'20',c:'23',v:'20',texto:"Não estejas entre os beberrões de vinho, nem entre os comilões de carne." },
{name:'202321',l:'20',c:'23',v:'21',texto:"Porque o beberrão e o comilão cairão em pobreza; e a sonolência faz trazer as vestes rotas." },
{name:'202322',l:'20',c:'23',v:'22',texto:"Ouve a teu pai, que te gerou, e não desprezes a tua mãe, quando vier a envelhecer." },
{name:'202323',l:'20',c:'23',v:'23',texto:"Compra a verdade e não a vendas; sim, a sabedoria, e a disciplina, e a prudência." },
{name:'202324',l:'20',c:'23',v:'24',texto:"Grandemente se regozijará o pai do justo, e o que gerar a um sábio se alegrará nele." },
{name:'202325',l:'20',c:'23',v:'25',texto:"Alegrem-se teu pai e tua mãe, e regozije-se a que te gerou." },
{name:'202326',l:'20',c:'23',v:'26',texto:"Dá-me, filho meu, o teu coração, e os teus olhos observem os meus caminhos." },
{name:'202327',l:'20',c:'23',v:'27',texto:"Porque cova profunda é a prostituta, e poço estreito, a estranha." },
{name:'202328',l:'20',c:'23',v:'28',texto:"Também ela, como um salteador, se põe a espreitar e multiplica entre os homens os iníquos." },
{name:'202329',l:'20',c:'23',v:'29',texto:"Para quem são os ais? Para quem, os pesares? Para quem, as pelejas? Para quem, as queixas? Para quem, as feridas sem causa? E para quem, os olhos vermelhos?" },
{name:'202330',l:'20',c:'23',v:'30',texto:"Para os que se demoram perto do vinho, para os que andam buscando bebida misturada." },
{name:'202331',l:'20',c:'23',v:'31',texto:"Não olhes para o vinho, quando se mostra vermelho, quando resplandece no copo e se escoa suavemente." },
{name:'202332',l:'20',c:'23',v:'32',texto:"No seu fim, morderá como a cobra e, como o basilisco, picará." },
{name:'202333',l:'20',c:'23',v:'33',texto:"Os teus olhos olharão para as mulheres estranhas, e o teu coração falará perversidades." },
{name:'202334',l:'20',c:'23',v:'34',texto:"E serás como o que dorme no meio do mar e como o que dorme no topo do mastro" },
{name:'202335',l:'20',c:'23',v:'35',texto:"e dirás: Espancaram-me, e não me doeu; bateram-me, e não o senti; quando virei a despertar? Ainda tornarei a buscá-la outra vez." },
],
numVersiculos: 35,
},

c24: {
versiculos:[
{name:'20241',l:'20',c:'24',v:'1',texto:"Não tenhas inveja dos homens malignos, nem desejes estar com eles," },
{name:'20242',l:'20',c:'24',v:'2',texto:"porque o seu coração medita a rapina, e os seus lábios falam maliciosamente." },
{name:'20243',l:'20',c:'24',v:'3',texto:"Com a sabedoria se edifica a casa, e com a inteligência ela se firma;" },
{name:'20244',l:'20',c:'24',v:'4',texto:"e pelo conhecimento se encherão as câmaras de todas as substâncias preciosas e deleitáveis." },
{name:'20245',l:'20',c:'24',v:'5',texto:"Um varão sábio é forte, e o varão de conhecimento consolida a força." },
{name:'20246',l:'20',c:'24',v:'6',texto:"Porque com conselhos prudentes tu farás a guerra; e há vitória na multidão dos conselheiros." },
{name:'20247',l:'20',c:'24',v:'7',texto:"É demasiadamente alta para o tolo toda a sabedoria; na porta não abrirá a boca." },
{name:'20248',l:'20',c:'24',v:'8',texto:"Aquele que cuida em fazer mal, mestre de maus intentos o chamarão." },
{name:'20249',l:'20',c:'24',v:'9',texto:"O pensamento do tolo é pecado, e é abominável aos homens o escarnecedor." },
{name:'202410',l:'20',c:'24',v:'10',texto:"Se te mostrares frouxo no dia da angústia, a tua força será pequena." },
{name:'202411',l:'20',c:'24',v:'11',texto:"Livra os que estão destinados à morte e salva os que são levados para a matança, se os puderes retirar." },
{name:'202412',l:'20',c:'24',v:'12',texto:"Se disseres: Eis que o não sabemos; porventura, aquele que pondera os corações não o considerará? E aquele que atenta para a tua alma não o saberá? Não pagará ele ao homem conforme a sua obra?" },
{name:'202413',l:'20',c:'24',v:'13',texto:"Come mel, meu filho, porque é bom, e o favo de mel, que é doce ao teu paladar." },
{name:'202414',l:'20',c:'24',v:'14',texto:"Tal será o conhecimento da sabedoria para a tua alma; se a achares, haverá para ti galardão, e não será cortada a tua expectação." },
{name:'202415',l:'20',c:'24',v:'15',texto:"Não espies a habitação do justo, ó ímpio, nem assoles a sua câmara." },
{name:'202416',l:'20',c:'24',v:'16',texto:"Porque sete vezes cairá o justo e se levantará; mas os ímpios tropeçarão no mal." },
{name:'202417',l:'20',c:'24',v:'17',texto:"Quando cair o teu inimigo, não te alegres, nem quando tropeçar se regozije o teu coração;" },
{name:'202418',l:'20',c:'24',v:'18',texto:"para que o SENHOR isso não veja, e seja mau aos seus olhos, e desvie dele a sua ira." },
{name:'202419',l:'20',c:'24',v:'19',texto:"Não te aflijas por causa dos malfeitores, nem tenhas inveja dos ímpios." },
{name:'202420',l:'20',c:'24',v:'20',texto:"Porque o maligno não terá galardão algum, e a lâmpada dos ímpios se apagará." },
{name:'202421',l:'20',c:'24',v:'21',texto:"Teme ao SENHOR, filho meu, e ao rei, e não te entremetas com os que buscam mudanças." },
{name:'202422',l:'20',c:'24',v:'22',texto:"Porque, de repente, se levantará a sua perdição, e a ruína deles, quem a conhecerá?" },
{name:'202423',l:'20',c:'24',v:'23',texto:"Também estes são provérbios dos sábios. Ter respeito a pessoas no juízo não é bom." },
{name:'202424',l:'20',c:'24',v:'24',texto:"O que disser ao ímpio: Justo és, os povos o amaldiçoarão, as nações o detestarão." },
{name:'202425',l:'20',c:'24',v:'25',texto:"Mas, para os que o repreenderem, haverá delícias, e sobre eles virá a bênção do bem." },
{name:'202426',l:'20',c:'24',v:'26',texto:"Beija com os lábios o que responde com palavras retas." },
{name:'202427',l:'20',c:'24',v:'27',texto:"Prepara fora a tua obra, e apronta-a no campo, e então edifica a tua casa." },
{name:'202428',l:'20',c:'24',v:'28',texto:"Não sejas testemunha sem causa contra o teu próximo; por que enganarias com os teus lábios?" },
{name:'202429',l:'20',c:'24',v:'29',texto:"Não digas: Como ele me fez a mim, assim lhe farei a ele; pagarei a cada um segundo a sua obra." },
{name:'202430',l:'20',c:'24',v:'30',texto:"Passei pelo campo do preguiçoso e junto à vinha do homem falto de entendimento;" },
{name:'202431',l:'20',c:'24',v:'31',texto:"e eis que toda estava cheia de cardos, e a sua superfície, coberta de urtigas, e a sua parede de pedra estava derribada." },
{name:'202432',l:'20',c:'24',v:'32',texto:"O que tendo eu visto, o considerei; e, vendo-o, recebi instrução." },
{name:'202433',l:'20',c:'24',v:'33',texto:"Um pouco de sono, adormecendo um pouco, encruzando as mãos outro pouco, para estar deitado," },
{name:'202434',l:'20',c:'24',v:'34',texto:"assim sobrevirá a tua pobreza como um ladrão, e a tua necessidade, como um homem armado." },
],
numVersiculos: 34,
},

c25: {
versiculos:[
{name:'20251',l:'20',c:'25',v:'1',texto:"Também estes são provérbios de Salomão, os quais transcreveram os homens de Ezequias, rei de Judá." },
{name:'20252',l:'20',c:'25',v:'2',texto:"A glória de Deus é encobrir o negócio, mas a glória dos reis é tudo investigar." },
{name:'20253',l:'20',c:'25',v:'3',texto:"Para a altura dos céus, e para a profundeza da terra, e para o coração dos reis, não há investigação alguma." },
{name:'20254',l:'20',c:'25',v:'4',texto:"Tira da prata as escórias, e sairá vaso para o fundidor." },
{name:'20255',l:'20',c:'25',v:'5',texto:"Tira o ímpio da presença do rei, e o seu trono se firmará na justiça." },
{name:'20256',l:'20',c:'25',v:'6',texto:"Não te glories na presença do rei, nem te ponhas no lugar dos grandes;" },
{name:'20257',l:'20',c:'25',v:'7',texto:"porque melhor é que te digam: Sobe para aqui, do que seres humilhado diante do príncipe a quem já os teus olhos viram." },
{name:'20258',l:'20',c:'25',v:'8',texto:"Não te apresses a litigar, para depois, ao fim, não saberes o que hás de fazer, podendo-te confundir o teu próximo." },
{name:'20259',l:'20',c:'25',v:'9',texto:"Pleiteia a tua causa com o teu próximo mesmo e não descubras o segredo de outro;" },
{name:'202510',l:'20',c:'25',v:'10',texto:"para que não te desonre o que o ouvir, não se apartando de ti a infâmia." },
{name:'202511',l:'20',c:'25',v:'11',texto:"Como maçãs de ouro em salvas de prata, assim é a palavra dita a seu tempo." },
{name:'202512',l:'20',c:'25',v:'12',texto:"Como pendentes de ouro e gargantilhas de ouro fino, assim é o sábio repreensor para o ouvido ouvinte." },
{name:'202513',l:'20',c:'25',v:'13',texto:"Como frieza de neve no tempo da sega, assim é o mensageiro fiel para com os que o enviam; porque alegra a alma dos seus senhores." },
{name:'202514',l:'20',c:'25',v:'14',texto:"Como nuvens e ventos que não trazem chuva, assim é o homem que se gaba falsamente de dádivas." },
{name:'202515',l:'20',c:'25',v:'15',texto:"Pela longanimidade se persuade o príncipe, e a língua branda quebranta os ossos." },
{name:'202516',l:'20',c:'25',v:'16',texto:"Achaste mel? Come o que te basta; para que, porventura, não te fartes dele e o venhas a vomitar." },
{name:'202517',l:'20',c:'25',v:'17',texto:"Retira o pé da casa do teu próximo, para que se não enfade de ti e te aborreça." },
{name:'202518',l:'20',c:'25',v:'18',texto:"Martelo, e espada, e flecha aguda é o homem que levanta falso testemunho contra o seu próximo." },
{name:'202519',l:'20',c:'25',v:'19',texto:"Como dente quebrado e pé deslocado, assim é a confiança no desleal, no tempo da angústia." },
{name:'202520',l:'20',c:'25',v:'20',texto:"O que entoa canções junto ao coração aflito é como aquele que se despe num dia de frio e como vinagre sobre salitre." },
{name:'202521',l:'20',c:'25',v:'21',texto:"Se o que te aborrece tiver fome, dá-lhe pão para comer; e, se tiver sede, dá-lhe água para beber," },
{name:'202522',l:'20',c:'25',v:'22',texto:"porque, assim, brasas lhe amontoarás sobre a cabeça; e o SENHOR to pagará." },
{name:'202523',l:'20',c:'25',v:'23',texto:"O vento norte afugenta a chuva, e a língua fingida, a face irada." },
{name:'202524',l:'20',c:'25',v:'24',texto:"Melhor é morar num canto de umas águas-furtadas do que com a mulher rixosa numa casa ampla." },
{name:'202525',l:'20',c:'25',v:'25',texto:"Como água fria para uma alma cansada, assim são as boas-novas de terra remota." },
{name:'202526',l:'20',c:'25',v:'26',texto:"Como fonte turva e manancial corrupto, assim é o justo que cai diante do ímpio." },
{name:'202527',l:'20',c:'25',v:'27',texto:"Comer muito mel não é bom; assim, a investigação da própria glória não é glória." },
{name:'202528',l:'20',c:'25',v:'28',texto:"Como a cidade derribada, que não tem muros, assim é o homem que não pode conter o seu espírito." },
],
numVersiculos: 28,
},

c26: {
versiculos:[
{name:'20261',l:'20',c:'26',v:'1',texto:"Como a neve no verão e como a chuva na sega, assim não é conveniente ao louco a honra." },
{name:'20262',l:'20',c:'26',v:'2',texto:"Como o pássaro no seu vaguear, e como a andorinha no seu vôo, assim a maldição sem causa não virá." },
{name:'20263',l:'20',c:'26',v:'3',texto:"O açoite é para o cavalo, o freio, para o jumento, e a vara, para as costas dos tolos." },
{name:'20264',l:'20',c:'26',v:'4',texto:"Não respondas ao tolo segundo a sua estultícia, para que também te não faças semelhante a ele." },
{name:'20265',l:'20',c:'26',v:'5',texto:"Responde ao tolo segundo a sua estultícia, para que não seja sábio aos seus olhos." },
{name:'20266',l:'20',c:'26',v:'6',texto:"Os pés corta e o dano bebe quem manda mensagens pelas mãos de um tolo." },
{name:'20267',l:'20',c:'26',v:'7',texto:"Como as pernas do coxo, que pendem frouxas, assim é o provérbio na boca dos tolos." },
{name:'20268',l:'20',c:'26',v:'8',texto:"Como o que prende a pedra preciosa na funda, assim é aquele que dá honra ao tolo." },
{name:'20269',l:'20',c:'26',v:'9',texto:"Como o espinho que entra na mão do ébrio, assim é o provérbio na boca dos tolos." },
{name:'202610',l:'20',c:'26',v:'10',texto:"Como um besteiro que a todos espanta, assim é o que assalaria os tolos e os transgressores." },
{name:'202611',l:'20',c:'26',v:'11',texto:"Como o cão que torna ao seu vômito, assim é o tolo que reitera a sua estultícia." },
{name:'202612',l:'20',c:'26',v:'12',texto:"Tens visto um homem que é sábio a seus próprios olhos? Maior esperança há no tolo do que nele." },
{name:'202613',l:'20',c:'26',v:'13',texto:"Diz o preguiçoso: Um leão está no caminho; um leão está nas ruas." },
{name:'202614',l:'20',c:'26',v:'14',texto:"Como a porta se revolve nos seus gonzos, assim o preguiçoso, na sua cama." },
{name:'202615',l:'20',c:'26',v:'15',texto:"O preguiçoso esconde a mão no seio; enfada-se de a levar à sua boca." },
{name:'202616',l:'20',c:'26',v:'16',texto:"Mais sábio é o preguiçoso a seus olhos do que sete homens que bem respondem." },
{name:'202617',l:'20',c:'26',v:'17',texto:"O que, passando, se mete em questão alheia é como aquele que toma um cão pelas orelhas." },
{name:'202618',l:'20',c:'26',v:'18',texto:"Como o louco que lança de si faíscas, flechas e mortandades," },
{name:'202619',l:'20',c:'26',v:'19',texto:"assim é o homem que engana o seu próximo e diz: Fiz isso por brincadeira." },
{name:'202620',l:'20',c:'26',v:'20',texto:"Sem lenha, o fogo se apagará; e, não havendo maldizente, cessará a contenda." },
{name:'202621',l:'20',c:'26',v:'21',texto:"Como o carvão é para o borralho, e a lenha, para o fogo, assim é o homem contencioso para acender rixas." },
{name:'202622',l:'20',c:'26',v:'22',texto:"As palavras do maldizente são como deliciosos bocados, que descem ao íntimo do ventre." },
{name:'202623',l:'20',c:'26',v:'23',texto:"Como o caco coberto de escórias de prata, assim são os lábios ardentes e o coração maligno." },
{name:'202624',l:'20',c:'26',v:'24',texto:"Aquele que aborrece dissimula com os seus lábios, mas no seu interior encobre o engano." },
{name:'202625',l:'20',c:'26',v:'25',texto:"Quando te suplicar com a sua voz, não te fies nele, porque sete abominações há no seu coração." },
{name:'202626',l:'20',c:'26',v:'26',texto:"Ainda que o seu ódio se encobre com engano, a sua malícia se descobrirá na congregação." },
{name:'202627',l:'20',c:'26',v:'27',texto:"O que faz uma cova nela cairá; e o que revolve a pedra, esta sobre ele rolará." },
{name:'202628',l:'20',c:'26',v:'28',texto:"A língua falsa aborrece aquele a quem ela tem maravilhado, e a boca lisonjeira opera a ruína." },
],
numVersiculos: 28,
},

c27: {
versiculos:[
{name:'20271',l:'20',c:'27',v:'1',texto:"Não presumas do dia de amanhã, porque não sabes o que produzirá o dia." },
{name:'20272',l:'20',c:'27',v:'2',texto:"Louve-te o estranho, e não a tua boca, o estrangeiro, e não os teus lábios." },
{name:'20273',l:'20',c:'27',v:'3',texto:"Pesada é a pedra, e a areia também; mas a ira do insensato é mais pesada do que elas ambas." },
{name:'20274',l:'20',c:'27',v:'4',texto:"Cruel é o furor e a impetuosa ira, mas quem parará perante a inveja?" },
{name:'20275',l:'20',c:'27',v:'5',texto:"Melhor é a repreensão aberta do que o amor encoberto." },
{name:'20276',l:'20',c:'27',v:'6',texto:"Fiéis são as feridas feitas pelo que ama, mas os beijos do que aborrece são enganosos." },
{name:'20277',l:'20',c:'27',v:'7',texto:"A alma farta pisa o favo de mel, mas à alma faminta todo amargo é doce." },
{name:'20278',l:'20',c:'27',v:'8',texto:"Qual ave que vagueia longe do seu ninho, tal é o homem que anda vagueando longe do seu lugar." },
{name:'20279',l:'20',c:'27',v:'9',texto:"O óleo e o perfume alegram o coração; assim a doença do amigo, com o conselho cordial." },
{name:'202710',l:'20',c:'27',v:'10',texto:"Não abandones o teu amigo, nem o amigo de teu pai, nem entres na casa de teu irmão no dia da tua adversidade; melhor é o vizinho perto do que o irmão longe." },
{name:'202711',l:'20',c:'27',v:'11',texto:"Sê sábio, filho meu, e alegra o meu coração, para que tenha alguma coisa que responder àquele que me desprezar." },
{name:'202712',l:'20',c:'27',v:'12',texto:"O avisado vê o mal e esconde-se; mas os simples passam e sofrem a pena." },
{name:'202713',l:'20',c:'27',v:'13',texto:"Quando alguém fica por fiador do estranho, toma-lhe tu a sua roupa e penhora-o pela estranha." },
{name:'202714',l:'20',c:'27',v:'14',texto:"O que bendiz ao seu amigo em alta voz, madrugando pela manhã, por maldição se lhe contará." },
{name:'202715',l:'20',c:'27',v:'15',texto:"O gotejar contínuo no dia de grande chuva e a mulher rixosa, um e outro são semelhantes." },
{name:'202716',l:'20',c:'27',v:'16',texto:"Aquele que a contivesse, conteria o vento; e a sua destra acomete o óleo." },
{name:'202717',l:'20',c:'27',v:'17',texto:"Como o ferro com o ferro se aguça, assim o homem afia o rosto do seu amigo." },
{name:'202718',l:'20',c:'27',v:'18',texto:"O que guarda a figueira comerá do seu fruto; e o que vela pelo seu senhor será honrado." },
{name:'202719',l:'20',c:'27',v:'19',texto:"Como na água o rosto corresponde ao rosto, assim o coração do homem ao homem." },
{name:'202720',l:'20',c:'27',v:'20',texto:"O inferno e a perdição nunca se fartam, e os olhos do homem nunca se satisfazem." },
{name:'202721',l:'20',c:'27',v:'21',texto:"O crisol é para a prata, e o forno, para o ouro, e o homem é provado pelos louvores." },
{name:'202722',l:'20',c:'27',v:'22',texto:"Ainda que pisasses o tolo com uma mão de gral entre grãos de cevada pilada, não se iria dele a sua estultícia." },
{name:'202723',l:'20',c:'27',v:'23',texto:"Procura conhecer o estado das tuas ovelhas; põe o teu coração sobre o gado." },
{name:'202724',l:'20',c:'27',v:'24',texto:"Porque as riquezas não duram para sempre; e duraria a coroa de geração em geração?" },
{name:'202725',l:'20',c:'27',v:'25',texto:"Quando se mostrar a erva, e aparecerem os renovos, então, ajunta as ervas dos montes." },
{name:'202726',l:'20',c:'27',v:'26',texto:"Os cordeiros serão para te vestires, e os bodes, para o preço do campo." },
{name:'202727',l:'20',c:'27',v:'27',texto:"E haverá bastante leite de cabras para o teu sustento, para sustento da tua casa e para sustento das tuas criadas." },
],
numVersiculos: 27,
},

c28: {
versiculos:[
{name:'20281',l:'20',c:'28',v:'1',texto:"Fogem os ímpios, sem que ninguém os persiga; mas qualquer justo está confiado como o filho do leão." },
{name:'20282',l:'20',c:'28',v:'2',texto:"Por causa da transgressão da terra, muitos são os seus príncipes, mas, por virtude de homens prudentes e sábios, ela continuará." },
{name:'20283',l:'20',c:'28',v:'3',texto:"O homem pobre que oprime os pobres é como chuva impetuosa, que não deixa nenhum trigo." },
{name:'20284',l:'20',c:'28',v:'4',texto:"Os que deixam a lei louvam o ímpio; mas os que guardam a lei pelejam contra eles." },
{name:'20285',l:'20',c:'28',v:'5',texto:"Os homens maus não entendem o juízo, mas os que buscam o SENHOR entendem tudo." },
{name:'20286',l:'20',c:'28',v:'6',texto:"Melhor é o pobre que anda na sua sinceridade do que o de caminhos perversos, ainda que seja rico." },
{name:'20287',l:'20',c:'28',v:'7',texto:"O que guarda a lei é filho sábio, mas o companheiro dos comilões envergonha a seu pai." },
{name:'20288',l:'20',c:'28',v:'8',texto:"O que aumenta a sua fazenda com usura e onzena ajunta-a para o que se compadece do pobre." },
{name:'20289',l:'20',c:'28',v:'9',texto:"O que desvia os seus ouvidos de ouvir a lei, até a sua oração será abominável." },
{name:'202810',l:'20',c:'28',v:'10',texto:"O que faz com que os retos se desviem para um mau caminho, ele mesmo cairá na sua cova; mas os sinceros herdarão o bem." },
{name:'202811',l:'20',c:'28',v:'11',texto:"O homem rico é sábio aos seus próprios olhos; mas o pobre que é sábio o examina." },
{name:'202812',l:'20',c:'28',v:'12',texto:"Quando os justos triunfam, há grande alegria; mas, quando os ímpios sobem, os homens escondem-se." },
{name:'202813',l:'20',c:'28',v:'13',texto:"O que encobre as suas transgressões nunca prosperará; mas o que as confessa e deixa alcançará misericórdia." },
{name:'202814',l:'20',c:'28',v:'14',texto:"Bem-aventurado o homem que continuamente teme; mas o que endurece o seu coração virá a cair no mal." },
{name:'202815',l:'20',c:'28',v:'15',texto:"Como leão bramidor e urso faminto, assim é o ímpio que domina sobre um povo pobre." },
{name:'202816',l:'20',c:'28',v:'16',texto:"O príncipe falto de inteligência também multiplica as opressões, mas o que aborrece a avareza prolongará os seus dias." },
{name:'202817',l:'20',c:'28',v:'17',texto:"O homem carregado do sangue de qualquer pessoa fugirá até à cova; ninguém o detenha." },
{name:'202818',l:'20',c:'28',v:'18',texto:"O que anda sinceramente salvar-se-á, mas o perverso em seus caminhos cairá logo." },
{name:'202819',l:'20',c:'28',v:'19',texto:"O que lavrar a sua terra virá a fartar-se de pão, mas o que segue a ociosos se fartará de pobreza." },
{name:'202820',l:'20',c:'28',v:'20',texto:"O homem fiel abundará em bênçãos, mas o que se apressa a enriquecer não ficará sem castigo." },
{name:'202821',l:'20',c:'28',v:'21',texto:"Ter respeito à aparência de pessoas não é bom, porque até por um bocado de pão o homem prevaricará." },
{name:'202822',l:'20',c:'28',v:'22',texto:"Aquele que tem um olho mau corre atrás das riquezas, mas não sabe que há de vir sobre ele a pobreza." },
{name:'202823',l:'20',c:'28',v:'23',texto:"O que repreende ao homem achará depois mais favor do que aquele que lisonjeia com a língua." },
{name:'202824',l:'20',c:'28',v:'24',texto:"O que rouba a seu pai ou a sua mãe e diz: Não há transgressão, companheiro é do destruidor." },
{name:'202825',l:'20',c:'28',v:'25',texto:"O altivo de ânimo levanta contendas, mas o que confia no SENHOR engordará." },
{name:'202826',l:'20',c:'28',v:'26',texto:"O que confia no seu próprio coração é insensato, mas o que anda sabiamente escapará." },
{name:'202827',l:'20',c:'28',v:'27',texto:"O que dá ao pobre não terá necessidade, mas o que esconde os olhos terá muitas maldições." },
{name:'202828',l:'20',c:'28',v:'28',texto:"Quando os ímpios sobem, os homens se escondem, mas, quando eles perecem, os justos se multiplicam." },
],
numVersiculos: 28,
},

c29: {
versiculos:[
{name:'20291',l:'20',c:'29',v:'1',texto:"O homem que muitas vezes repreendido endurece a cerviz será quebrantado de repente sem que haja cura." },
{name:'20292',l:'20',c:'29',v:'2',texto:"Quando os justos se engrandecem, o povo se alegra, mas, quando o ímpio domina, o povo suspira." },
{name:'20293',l:'20',c:'29',v:'3',texto:"O homem que ama a sabedoria alegra a seu pai, mas o companheiro de prostitutas desperdiça a fazenda." },
{name:'20294',l:'20',c:'29',v:'4',texto:"O rei com juízo sustém a terra, mas o amigo de subornos a transtorna." },
{name:'20295',l:'20',c:'29',v:'5',texto:"O homem que lisonjeia a seu próximo arma uma rede aos seus passos." },
{name:'20296',l:'20',c:'29',v:'6',texto:"Na transgressão do homem mau há laço, mas o justo canta e regozija-se." },
{name:'20297',l:'20',c:'29',v:'7',texto:"Informa-se o justo da causa dos pobres, mas o ímpio não compreende isso." },
{name:'20298',l:'20',c:'29',v:'8',texto:"Os homens escarnecedores abrasam a cidade, mas os sábios desviam a ira." },
{name:'20299',l:'20',c:'29',v:'9',texto:"O homem sábio que pleiteia com o tolo, quer se perturbe quer se ria, não terá descanso." },
{name:'202910',l:'20',c:'29',v:'10',texto:"Os homens sanguinários aborrecem aquele que é sincero, mas os retos procuram o seu bem." },
{name:'202911',l:'20',c:'29',v:'11',texto:"Um tolo expande toda a sua ira, mas o sábio a encobre e reprime." },
{name:'202912',l:'20',c:'29',v:'12',texto:"O governador que dá atenção às palavras mentirosas achará que todos os seus servos são ímpios." },
{name:'202913',l:'20',c:'29',v:'13',texto:"O pobre e o usurário se encontram, e o SENHOR alumia os olhos de ambos." },
{name:'202914',l:'20',c:'29',v:'14',texto:"O rei, que julga os pobres conforme a verdade, firmará o seu trono para sempre." },
{name:'202915',l:'20',c:'29',v:'15',texto:"A vara e a repreensão dão sabedoria, mas o rapaz entregue a si mesmo envergonha a sua mãe." },
{name:'202916',l:'20',c:'29',v:'16',texto:"Quando os ímpios se multiplicam, multiplicam-se as transgressões, mas os justos verão a sua queda." },
{name:'202917',l:'20',c:'29',v:'17',texto:"Castiga o teu filho, e te fará descansar e dará delícias à tua alma." },
{name:'202918',l:'20',c:'29',v:'18',texto:"Não havendo profecia, o povo se corrompe; mas o que guarda a lei, esse é bem-aventurado." },
{name:'202919',l:'20',c:'29',v:'19',texto:"O servo não se emendará com palavras, porque, ainda que te entenda, não te atenderá." },
{name:'202920',l:'20',c:'29',v:'20',texto:"Tens visto um homem precipitado nas suas palavras? Maior esperança há de um tolo do que dele." },
{name:'202921',l:'20',c:'29',v:'21',texto:"Quando alguém cria delicadamente o seu servo desde a mocidade, por derradeiro ele quererá ser seu filho." },
{name:'202922',l:'20',c:'29',v:'22',texto:"O homem iracundo levanta contendas; e o furioso multiplica as transgressões." },
{name:'202923',l:'20',c:'29',v:'23',texto:"A soberba do homem o abaterá, mas o humilde de espírito obterá honra." },
{name:'202924',l:'20',c:'29',v:'24',texto:"O que tem parte com o ladrão aborrece a sua própria alma; ouve maldições e não o denuncia." },
{name:'202925',l:'20',c:'29',v:'25',texto:"O receio do homem armará laços, mas o que confia no SENHOR será posto em alto retiro." },
{name:'202926',l:'20',c:'29',v:'26',texto:"Muitos buscam a face do príncipe, mas o juízo de cada um vem do SENHOR." },
{name:'202927',l:'20',c:'29',v:'27',texto:"Abominação é para os justos o homem iníquo, e abominação é para o ímpio o de retos caminhos." },
],
numVersiculos: 27,
},

c30: {
versiculos:[
{name:'20301',l:'20',c:'30',v:'1',texto:"Palavras de Agur, filho de Jaque, o oráculo. Disse este varão a Itiel, a Itiel e a Ucal:" },
{name:'20302',l:'20',c:'30',v:'2',texto:"Na verdade, que eu sou mais bruto do que ninguém; não tenho o entendimento do homem," },
{name:'20303',l:'20',c:'30',v:'3',texto:"nem aprendi a sabedoria, nem tenho o conhecimento do Santo." },
{name:'20304',l:'20',c:'30',v:'4',texto:"Quem subiu ao céu e desceu? Quem encerrou os ventos nos seus punhos? Quem amarrou as águas na sua roupa? Quem estabeleceu todas as extremidades da terra? Qual é o seu nome, e qual é o nome de seu filho, se é que o sabes?" },
{name:'20305',l:'20',c:'30',v:'5',texto:"Toda palavra de Deus é pura; escudo é para os que confiam nele." },
{name:'20306',l:'20',c:'30',v:'6',texto:"Nada acrescentes às suas palavras, para que não te repreenda, e sejas achado mentiroso." },
{name:'20307',l:'20',c:'30',v:'7',texto:"Duas coisas te pedi; não mas negues, antes que morra:" },
{name:'20308',l:'20',c:'30',v:'8',texto:"afasta de mim a vaidade e a palavra mentirosa; não me dês nem a pobreza nem a riqueza; mantém-me do pão da minha porção acostumada;" },
{name:'20309',l:'20',c:'30',v:'9',texto:"para que, porventura, de farto te não negue e diga: Quem é o SENHOR? Ou que, empobrecendo, venha a furtar e lance mão do nome de Deus." },
{name:'203010',l:'20',c:'30',v:'10',texto:"Não calunies o servo diante de seu senhor, para que te não amaldiçoe e fiques culpado." },
{name:'203011',l:'20',c:'30',v:'11',texto:"Há uma geração que amaldiçoa a seu pai e que não bendiz a sua mãe." },
{name:'203012',l:'20',c:'30',v:'12',texto:"Há uma geração que é pura aos seus olhos e que nunca foi lavada da sua imundícia." },
{name:'203013',l:'20',c:'30',v:'13',texto:"Há uma geração cujos olhos são altivos e cujas pálpebras são levantadas para cima." },
{name:'203014',l:'20',c:'30',v:'14',texto:"Há uma geração cujos dentes são espadas e cujos queixais são facas, para consumirem na terra os aflitos e os necessitados entre os homens." },
{name:'203015',l:'20',c:'30',v:'15',texto:"A sanguessuga tem duas filhas, a saber: Dá, Dá. Estas três coisas nunca se fartam; e quatro nunca dizem: Basta:" },
{name:'203016',l:'20',c:'30',v:'16',texto:"a sepultura, a madre estéril, a terra, que se não farta de água, e o fogo, que nunca diz: Basta." },
{name:'203017',l:'20',c:'30',v:'17',texto:"Os olhos que zombam do pai ou desprezam a obediência da mãe, corvos do ribeiro os arrancarão, e os pintãos da águia os comerão." },
{name:'203018',l:'20',c:'30',v:'18',texto:"Há três coisas que me maravilham, e a quarta não a conheço:" },
{name:'203019',l:'20',c:'30',v:'19',texto:"o caminho da águia no céu, o caminho da cobra na penha, o caminho do navio no meio do mar e o caminho do homem com uma virgem." },
{name:'203020',l:'20',c:'30',v:'20',texto:"Tal é o caminho da mulher adúltera: ela come, e limpa a sua boca, e diz: Não cometi maldade." },
{name:'203021',l:'20',c:'30',v:'21',texto:"Por três coisas se alvoroça a terra, e a quarta não a pode suportar:" },
{name:'203022',l:'20',c:'30',v:'22',texto:"pelo servo, quando reina; e pelo tolo, quando anda farto de pão;" },
{name:'203023',l:'20',c:'30',v:'23',texto:"pela mulher aborrecida, quando se casa; e pela serva, quando fica herdeira da sua senhora." },
{name:'203024',l:'20',c:'30',v:'24',texto:"Estas quatro coisas são das mais pequenas da terra, mas sábias, bem providas de sabedoria:" },
{name:'203025',l:'20',c:'30',v:'25',texto:"as formigas são um povo impotente; todavia, no verão preparam a sua comida;" },
{name:'203026',l:'20',c:'30',v:'26',texto:"os coelhos são um povo débil; e, contudo, fazem a sua casa nas rochas;" },
{name:'203027',l:'20',c:'30',v:'27',texto:"os gafanhotos não têm rei; e, contudo, todos saem e em bandos se repartem;" },
{name:'203028',l:'20',c:'30',v:'28',texto:"a aranha, que se apanha com as mãos e está nos paços dos reis." },
{name:'203029',l:'20',c:'30',v:'29',texto:"Há três que têm um bom andar, e o quarto passeia muito bem:" },
{name:'203030',l:'20',c:'30',v:'30',texto:"o leão, o mais forte entre os animais, que por ninguém torna atrás;" },
{name:'203031',l:'20',c:'30',v:'31',texto:"o cavalo de guerra, bem cingido pelos lombos; o bode também; e o rei, a quem se não pode resistir." },
{name:'203032',l:'20',c:'30',v:'32',texto:"Se procedeste loucamente, elevando-te, e se imaginaste o mal, põe a mão na boca." },
{name:'203033',l:'20',c:'30',v:'33',texto:"Porque o espremer do leite produz manteiga, e o espremer do nariz produz sangue, e o espremer da ira produz contenda." },
],
numVersiculos: 33,
},

c31: {
versiculos:[
{name:'20311',l:'20',c:'31',v:'1',texto:"Palavras do rei Lemuel, a profecia que lhe ensinou sua mãe." },
{name:'20312',l:'20',c:'31',v:'2',texto:"Como, filho meu? E como, ó filho do meu ventre? E como, ó filho das minhas promessas?" },
{name:'20313',l:'20',c:'31',v:'3',texto:"Não dês às mulheres a tua força, nem os teus caminhos, ao que destrói os reis." },
{name:'20314',l:'20',c:'31',v:'4',texto:"Não é próprio dos reis, ó Lemuel, não é próprio dos reis beber vinho, nem dos príncipes desejar bebida forte." },
{name:'20315',l:'20',c:'31',v:'5',texto:"Para que não bebam, e se esqueçam do estatuto, e pervertam o juízo de todos os aflitos." },
{name:'20316',l:'20',c:'31',v:'6',texto:"Dai bebida forte aos que perecem, e o vinho, aos amargosos de espírito;" },
{name:'20317',l:'20',c:'31',v:'7',texto:"para que bebam, e se esqueçam da sua pobreza, e do seu trabalho não se lembrem mais." },
{name:'20318',l:'20',c:'31',v:'8',texto:"Abre a tua boca a favor do mudo, pelo direito de todos os que se acham em desolação." },
{name:'20319',l:'20',c:'31',v:'9',texto:"Abre a tua boca, julga retamente e faze justiça aos pobres e aos necessitados." },
{name:'203110',l:'20',c:'31',v:'10',texto:"Mulher virtuosa, quem a achará? O seu valor muito excede o de rubins." },
{name:'203111',l:'20',c:'31',v:'11',texto:"O coração do seu marido está nela confiado, e a ela nenhuma fazenda faltará." },
{name:'203112',l:'20',c:'31',v:'12',texto:"Ela lhe faz bem e não mal, todos os dias da sua vida." },
{name:'203113',l:'20',c:'31',v:'13',texto:"Busca lã e linho e trabalha de boa vontade com as suas mãos." },
{name:'203114',l:'20',c:'31',v:'14',texto:"É como o navio mercante: de longe traz o seu pão." },
{name:'203115',l:'20',c:'31',v:'15',texto:"Ainda de noite, se levanta e dá mantimento à sua casa e a tarefa às suas servas." },
{name:'203116',l:'20',c:'31',v:'16',texto:"Examina uma herdade e adquire-a; planta uma vinha com o fruto de suas mãos." },
{name:'203117',l:'20',c:'31',v:'17',texto:"Cinge os lombos de força e fortalece os braços." },
{name:'203118',l:'20',c:'31',v:'18',texto:"Prova e vê que é boa sua mercadoria; e a sua lâmpada não se apaga de noite." },
{name:'203119',l:'20',c:'31',v:'19',texto:"Estende as mãos ao fuso, e as palmas das suas mãos pegam na roca." },
{name:'203120',l:'20',c:'31',v:'20',texto:"Abre a mão ao aflito; e ao necessitado estende as mãos." },
{name:'203121',l:'20',c:'31',v:'21',texto:"Não temerá, por causa da neve, porque toda a sua casa anda forrada de roupa dobrada." },
{name:'203122',l:'20',c:'31',v:'22',texto:"Faz para si tapeçaria; de linho fino e de púrpura é a sua veste." },
{name:'203123',l:'20',c:'31',v:'23',texto:"Conhece-se o seu marido nas portas, quando se assenta com os anciãos da terra." },
{name:'203124',l:'20',c:'31',v:'24',texto:"Faz panos de linho fino, e vende-os, e dá cintas aos mercadores." },
{name:'203125',l:'20',c:'31',v:'25',texto:"A força e a glória são as suas vestes, e ri-se do dia futuro." },
{name:'203126',l:'20',c:'31',v:'26',texto:"Abre a boca com sabedoria, e a lei da beneficência está na sua língua." },
{name:'203127',l:'20',c:'31',v:'27',texto:"Olha pelo governo de sua casa e não come o pão da preguiça." },
{name:'203128',l:'20',c:'31',v:'28',texto:"Levantam-se seus filhos, e chamam-na bem-aventurada; como também seu marido, que a louva, dizendo:" },
{name:'203129',l:'20',c:'31',v:'29',texto:"Muitas filhas agiram virtuosamente, mas tu a todas és superior." },
{name:'203130',l:'20',c:'31',v:'30',texto:"Enganosa é a graça, e vaidade, a formosura, mas a mulher que teme ao SENHOR, essa será louvada." },
{name:'203131',l:'20',c:'31',v:'31',texto:"Dai-lhe do fruto das suas mãos, e louvem-na nas portas as suas obras." },
],
numVersiculos: 31,
},


};
